mapper.insert:
		INSERT INTO GW_EA_INCOMPANY
        (
        	APPROVAL_SEQ, RECEPTION, SEND, REFERENCE, PURPOSE, CONTENT, ETC
        )
     	VALUES
     	(
     		#{approval_seq}, #{reception}, #{send}, #{reference}, #{purpose}, #{content}, #{etc}
        )

mapper.select:
		SELECT APPROVAL_SEQ, RECEPTION, SEND, REFERENCE, PURPOSE, CONTENT, ETC
		  FROM GW_EA_INCOMPANY
    	 WHERE APPROVAL_SEQ = #{approval_seq}

mapper.update:
		UPDATE GW_EA_INCOMPANY 
		   SET RECEPTION = #{reception}, SEND = #{send}, REFERENCE = #{reference}, PURPOSE = #{purpose}, CONTENT = #{content}, ETC = #{etc}
    	 WHERE APPROVAL_SEQ = #{approval_seq}
