mapper.select:
-- 거래처 잔고/담보 현황 목록 / getCustomerBalanceGridDetail / @business_customerBalance.xml / 영업관리-잔고담보현황-조회(우측jqGrid)
SELECT * FROM (SELECT T0.*, FLOOR((ROWNUM - 1) / #{perPageRow,jdbcType=VARCHAR} + 1) PAGE FROM (
SELECT A.CUSTCODE AS CUST_ID, A.SEQ AS INPUT_SEQ, A.SCRTAMT AS SALE_DAMBO_AMT, '' AS BUY_DAMBO_AMT, A.EXPDATE AS END_YMD, A.FIXDATE AS START_YMD, A.GUARANTOR AS BALHANG, A.OBJECTADDR AS JIGEUB, A.BILLNO AS BILL_NO, (SELECT DIVNAME FROM ORAGMP.CMCOMMONM WHERE CMMCODE = 'SL04' AND DIVCODE = A.SCRTDIV) AS BILL_GB, A.REMARK AS BIGO, A.INSERTDT AS INPUT_YMD, '' AS CHULGO_YMD, B.CUSTNAME AS CUST_NM FROM ORAGMP.CMCUSTSCRTD A, ORAGMP.CMCUSTM B WHERE A.CUSTCODE = B.CUSTCODE AND A.CUSTCODE = #{customerId,jdbcType=VARCHAR}
ORDER BY ${sidx} ${sord}
) T0 ) WHERE PAGE = #{page,jdbcType=VARCHAR}
