mapper.select:
-- 거래처 목록 / getCustomerInfoGridList / @business_customerInfo.xml / 영업관리 - 고객등록 - 거래처 검색 후 - 조회
SELECT * FROM (SELECT T0.*, FLOOR((ROWNUM - 1) / #{perPageRow} + 1) PAGE FROM (
SELECT A.CUSTCODE AS CUST_ID, A.CUSTNAME AS CUST_NM, A.CUSTCODE AS CUST_ID2, A.EMPCODE AS SAWON_ID, B.EMPNAME AS SAWON_NM FROM ORAGMP.CMCUSTM A, ORAGMP.CMEMPM B WHERE A.CUSTCODE LIKE '%'||#{as_cust_id}||'%' AND A.SAGODIV LIKE DECODE(#{as_use_yn}, 'Y', '1', '2') AND A.EMPCODE = B.EMPCODE
ORDER BY ${sidx} ${sord}
) T0 ) WHERE PAGE = #{page}

-- 고객 목록 / getClientGridList / @business_customerInfo.xml / 영업관리 - 고객등록 - 조회 - 그리드 리스트
SELECT * FROM (SELECT T0.*, FLOOR((ROWNUM - 1) / #{perPageRow,jdbcType=INTEGER} + 1) PAGE FROM (
SELECT B.CUSTCODE AS CUST_ID, A.SFA_CLIENT_NO AS CUSTOMER_ID, A.CLIENT_NAME AS CUSTOMER_NM, DECODE(A.MALE, '1', 'M', (DECODE(A.MALE, '2', 'F', NULL))) AS SEX, DECODE(A.MARRY_YN, '1', 'Y', DECODE(A.MARRY_YN, '2', 'N', NULL)) AS MARRY_YN, A.BIRTHDAY AS BIRTH_DAY, '' AS ACT_BIRTH_DAY, A.MARRY_DAY AS MARRY_DAY, '' AS CHILD_KIND, '' AS DISPOSITION, '' AS RELIGION, '' AS HIGHSCHOOL, A.LASTSCHOOLNM AS UNIVERSITY, A.ZIP_CODE AS ZIP, A.DETAIL_ADDR1 AS ADDRESS1, A.DETAIL_ADDR2 AS ADDRESS2, A.TEL_NO AS TEL, A.HP_NO AS MOBILE, '' AS FAX, A.EMAIL AS EMAIL, '' AS CAR_NO, '' AS CAR_COLOR, '' AS FOREIGN_STUDY_YN, A.POSITION_NAME AS RANK, A.MAJORSUBJECT AS LESSON, '' AS HOSPITAL, A.CLIENT_DEPT AS MAJOR, '' AS MAIN_BUYING, DECODE(A.FRIENDSHIP, '5', '매우친함', '4', '친함', '3', '보통', '2', '안 친함', '1', '매우 안 친함', '0', '미선택'), A.HOBBY AS HOBBY, '' AS TABOO_LIST, A.GITA AS GITA, A.HIRACODE AS hiracode FROM SALE.SFA_HIRA_CUSTOMER A, ORAGMP.CMCUSTM B WHERE A.HIRACODE = B.HIRACODE AND B.CUSTCODE = #{as_cust_id,jdbcType=VARCHAR}
ORDER BY ${sidx} ${sord}
) T0 ) WHERE PAGE = #{page,jdbcType=INTEGER}

-- 거래처 상세 목록 / getCustomerDetailEtcGridList / @business_customerInfo.xml / 영업관리 - 고객등록 -  조회 - 가운데 디테일 버튼

		SELECT *
			FROM (SELECT T0.*, FLOOR((ROWNUM - 1) / #{perPageRow,jdbcType=INTEGER} + 1) PAGE
        		FROM (


		SELECT CUSTCODE AS CUST_ID, '' AS SEQ, MEMO1 AS GITA
		FROM ORAGMP.CMCUSTM WHERE CUSTCODE = #{cust_id,jdbcType=VARCHAR}


					ORDER BY ${sidx} ${sord}



				) T0
			)
			WHERE PAGE = #{page,jdbcType=INTEGER}

-- 거래처 상세 목록 총 수 / getCustomerDetailEtcGridTotalCount / @business_customerInfo.xml / 영업관리 - 고객등록 -  조회 - 가운데 디테일 버튼

	SELECT COUNT(*) total_cnt
	FROM ORAGMP.CMCUSTM WHERE CUSTCODE = #{cust_id,jdbcType=VARCHAR}

mapper.update:
-- 거래처 상세정보 수정 / updateCustomerDetailEtc / @business_customerInfo.xml / 영업관리 - 고객등록 -  조회 - 가운데 디테일 버튼 - 항목 수정
UPDATE ORAGMP.CMCUSTM
SET MEMO1    = #{gita,jdbcType=VARCHAR}
WHERE CUSTCODE = #{cust_id,jdbcType=VARCHAR}

mapper.select:

		SELECT *
			FROM (SELECT T0.*, FLOOR((ROWNUM - 1) / #{perPageRow,jdbcType=INTEGER} + 1) PAGE
        		FROM (


						SELECT A.CUST_ID, 
						       A.CUSTOMER_ID, 
						       A.SEQ, 
						       A.HAKHEO_NM, 
						       A.DATEF, 
						       A.DATET, 
						       A.JIWI, 
						       A.GWANSIMDO, 
						       A.GITA
						  FROM SALE.CRM_HAKHEO A
						 WHERE A.CUST_ID = #{as_cust_id,jdbcType=VARCHAR}
						   AND A.CUSTOMER_ID = #{as_customer_id,jdbcType=VARCHAR}


					ORDER BY ${sidx} ${sord}



				) T0
			)
			WHERE PAGE = #{page,jdbcType=INTEGER}


SELECT COUNT(*) total_cnt 
  FROM SALE.CRM_HAKHEO A
 WHERE A.CUST_ID = #{as_cust_id,jdbcType=VARCHAR}
   AND A.CUSTOMER_ID = #{as_customer_id,jdbcType=VARCHAR}


		SELECT *
