mapper.insert:
		INSERT INTO GW_EA_CERTIFICATE
        (
        	SEQ, APPROVAL_SEQ, CERTI_NM, CERTI_CD, QTY, YONGDO
        )
     	VALUES
     	(
     		SEQ_GW_EA_CERTIFICATE.nextval, #{approval_seq}, #{certi_nm}, #{certi_cd}, #{qty}, #{yongdo}
        )

mapper.select:
	SELECT SEQ, APPROVAL_SEQ, CERTI_NM, CERTI_CD, QTY, YONGDO
   	  FROM GW_EA_CERTIFICATE
   	 WHERE APPROVAL_SEQ = #{approval_seq}
