mapper.select:
/*거래처 조회*/
SELECT * FROM (SELECT T0.*, FLOOR((ROWNUM - 1) / #{perPageRow,jdbcType=INTEGER} + 1) PAGE FROM (
SELECT CUST_ID , CUST_NM , DEPT_NM , SAWON_NM FROM( SELECT A.CUSTCODE AS CUST_ID , A.CUSTNAME AS CUST_NM , oragmp.fncommonnm('dept',C.DEPTCODE,'') AS DEPT_NM , C.EMPNAME AS SAWON_NM FROM ORAGMP.CMCUSTM A,ORAGMP.SLPROMPRCM B,ORAGMP.CMEMPM C WHERE A.EMPCODE = C.EMPCODE AND A.CUSTCODE = B.CUSTCODE
AND A.CUSTCODE = #{as_fr_cust,jdbcType=VARCHAR}
AND C.EMPCODE IN( SELECT EMPCODE FROM ORAGMP.CMEMPM WHERE OUTYN = 'N' AND DEPTCODE IN( SELECT DEPTCODE FROM ORAGMP.CMDEPTM WHERE USEYN = 'Y' AND #{as_assgnCode} in ('27010','27018','27023','27025','27026') CONNECT BY PRIOR DEPTCODE = PREDEPTCODE START WITH PREDEPTCODE = (SELECT B.PREDEPTCODE FROM ORAGMP.CMEMPM A , ORAGMP.CMDEPTM B WHERE A.EMPCODE = #{as_emp_cd} AND A.DEPTCODE = B.DEPTCODE )
UNION ALL SELECT DEPTCODE FROM ORAGMP.CMEMPM WHERE DEPTCODE = #{as_dept_cd} AND #{as_assgnCode} in ('27027','27030','27035') ) UNION ALL SELECT #{as_emp_cd} FROM DUAL WHERE #{as_assgnCode} IN ('27040','27050') ) UNION SELECT A.CUSTCODE AS CUST_ID , A.CUSTNAME AS CUST_NM , oragmp.fncommonnm('dept',C.DEPTCODE,'') AS DEPT_NM , C.EMPNAME AS SAWON_NM FROM ORAGMP.CMCUSTM A,ORAGMP.CMEMPM C WHERE A.CUSTCODE NOT IN( SELECT CUSTCODE FROM ORAGMP.SLPROMPRCM UNION SELECT ECUSTCODE FROM ORAGMP.SLPROMPRCM) AND A.EMPCODE = C.EMPCODE
AND A.CUSTCODE = #{as_fr_cust,jdbcType=VARCHAR}
AND C.EMPCODE IN( SELECT EMPCODE FROM ORAGMP.CMEMPM WHERE OUTYN = 'N' AND DEPTCODE IN( SELECT DEPTCODE FROM ORAGMP.CMDEPTM WHERE USEYN = 'Y' AND #{as_assgnCode} in ('27010','27018','27023','27025','27026') CONNECT BY PRIOR DEPTCODE = PREDEPTCODE START WITH PREDEPTCODE = (SELECT B.PREDEPTCODE FROM ORAGMP.CMEMPM A , ORAGMP.CMDEPTM B WHERE A.EMPCODE = #{as_emp_cd} AND A.DEPTCODE = B.DEPTCODE )
UNION ALL SELECT DEPTCODE FROM ORAGMP.CMEMPM WHERE DEPTCODE = #{as_dept_cd} AND #{as_assgnCode} in ('27027','27030','27035') ) UNION ALL SELECT #{as_emp_cd} FROM DUAL WHERE #{as_assgnCode} IN ('27040','27050') ) ) GROUP BY CUST_ID , CUST_NM , DEPT_NM , SAWON_NM
ORDER BY ${sidx} ${sord}
) T0 ) WHERE PAGE = #{page,jdbcType=INTEGER}

/* 거래처 총수*/ SELECT COUNT(DISTINCT(CUST_ID)) AS total_cnt FROM( SELECT A.CUSTCODE AS CUST_ID , A.CUSTNAME AS CUST_NM , oragmp.fncommonnm('dept',C.DEPTCODE,'') AS DEPT_NM , C.EMPNAME AS SAWON_NM FROM ORAGMP.CMCUSTM A,ORAGMP.SLPROMPRCM B,ORAGMP.CMEMPM C WHERE A.EMPCODE = C.EMPCODE AND A.CUSTCODE = B.CUSTCODE
AND A.CUSTCODE = #{as_fr_cust,jdbcType=VARCHAR}
AND C.EMPCODE IN( SELECT EMPCODE FROM ORAGMP.CMEMPM WHERE OUTYN = 'N' AND DEPTCODE IN( SELECT DEPTCODE FROM ORAGMP.CMDEPTM WHERE USEYN = 'Y' AND #{as_assgnCode} in ('27010','27018','27023','27025','27026') CONNECT BY PRIOR DEPTCODE = PREDEPTCODE START WITH PREDEPTCODE = (SELECT B.PREDEPTCODE FROM ORAGMP.CMEMPM A , ORAGMP.CMDEPTM B WHERE A.EMPCODE = #{as_emp_cd} AND A.DEPTCODE = B.DEPTCODE )
UNION ALL SELECT DEPTCODE FROM ORAGMP.CMEMPM WHERE DEPTCODE = #{as_dept_cd} AND #{as_assgnCode} in ('27027','27030','27035') ) UNION ALL SELECT #{as_emp_cd} FROM DUAL WHERE #{as_assgnCode} IN ('27040','27050') ) UNION SELECT A.CUSTCODE AS CUST_ID , A.CUSTNAME AS CUST_NM , oragmp.fncommonnm('dept',C.DEPTCODE,'') AS DEPT_NM , C.EMPNAME AS SAWON_NM FROM ORAGMP.CMCUSTM A,ORAGMP.CMEMPM C WHERE A.CUSTCODE NOT IN( SELECT CUSTCODE FROM ORAGMP.SLPROMPRCM UNION SELECT ECUSTCODE FROM ORAGMP.SLPROMPRCM) AND A.EMPCODE = C.EMPCODE
AND A.CUSTCODE = #{as_fr_cust,jdbcType=VARCHAR}
AND C.EMPCODE IN( SELECT EMPCODE FROM ORAGMP.CMEMPM WHERE OUTYN = 'N' AND DEPTCODE IN( SELECT DEPTCODE FROM ORAGMP.CMDEPTM WHERE USEYN = 'Y' AND #{as_assgnCode} in ('27010','27018','27023','27025','27026') CONNECT BY PRIOR DEPTCODE = PREDEPTCODE START WITH PREDEPTCODE = (SELECT B.PREDEPTCODE FROM ORAGMP.CMEMPM A , ORAGMP.CMDEPTM B WHERE A.EMPCODE = #{as_emp_cd} AND A.DEPTCODE = B.DEPTCODE )
UNION ALL SELECT DEPTCODE FROM ORAGMP.CMEMPM WHERE DEPTCODE = #{as_dept_cd} AND #{as_assgnCode} in ('27027','27030','27035') ) UNION ALL SELECT #{as_emp_cd} FROM DUAL WHERE #{as_assgnCode} IN ('27040','27050') ) )

/*거래처 별 원장 목록 상단 Grid */
SELECT * FROM (SELECT T0.*, FLOOR((ROWNUM - 1) / #{perPageRow,jdbcType=INTEGER} + 1) PAGE FROM (
SELECT A.CUSTCODE AS CUST_ID , A.CUSTNAME AS CUST_NM , oragmp.fncommonnm('dept',C.DEPTCODE,'') AS DEPT_NM , C.EMPNAME AS SAWON_NM FROM ORAGMP.CMCUSTM A,ORAGMP.SLPROMPRCM B,ORAGMP.CMEMPM C WHERE A.EMPCODE = C.EMPCODE AND A.CUSTCODE = B.ECUSTCODE
AND A.CUSTCODE = #{as_fr_cust,jdbcType=VARCHAR}
AND C.EMPCODE IN( SELECT EMPCODE FROM ORAGMP.CMEMPM WHERE OUTYN = 'N' AND DEPTCODE IN( SELECT DEPTCODE FROM ORAGMP.CMDEPTM WHERE USEYN = 'Y' AND #{as_assgnCode} in ('27010','27018','27023','27025','27026') CONNECT BY PRIOR DEPTCODE = PREDEPTCODE START WITH PREDEPTCODE = (SELECT B.PREDEPTCODE FROM ORAGMP.CMEMPM A , ORAGMP.CMDEPTM B WHERE A.EMPCODE = #{as_emp_cd} AND A.DEPTCODE = B.DEPTCODE )
UNION ALL SELECT DEPTCODE FROM ORAGMP.CMEMPM WHERE DEPTCODE = #{as_dept_cd} AND #{as_assgnCode} in ('27027','27030','27035') ) UNION ALL SELECT #{as_emp_cd} FROM DUAL WHERE #{as_assgnCode} IN ('27040','27050') ) GROUP BY A.CUSTCODE , A.CUSTNAME , C.DEPTCODE , C.EMPNAME
ORDER BY ${sidx} ${sord}
) T0 ) WHERE PAGE = #{page,jdbcType=INTEGER}

/*거래처별 원장목록 상단 Grid 총 수 */ SELECT COUNT(DISTINCT(A.CUSTCODE)) AS total_cnt FROM ORAGMP.CMCUSTM A,ORAGMP.SLPROMPRCM B,ORAGMP.CMEMPM C WHERE A.EMPCODE = C.EMPCODE AND A.CUSTCODE = B.ECUSTCODE
AND A.CUSTCODE = #{as_fr_cust,jdbcType=VARCHAR}
AND C.EMPCODE IN( SELECT EMPCODE FROM ORAGMP.CMEMPM WHERE OUTYN = 'N' AND DEPTCODE IN( SELECT DEPTCODE FROM ORAGMP.CMDEPTM WHERE USEYN = 'Y' AND #{as_assgnCode} in ('27010','27018','27023','27025','27026') CONNECT BY PRIOR DEPTCODE = PREDEPTCODE START WITH PREDEPTCODE = (SELECT B.PREDEPTCODE FROM ORAGMP.CMEMPM A , ORAGMP.CMDEPTM B WHERE A.EMPCODE = #{as_emp_cd} AND A.DEPTCODE = B.DEPTCODE )
UNION ALL SELECT DEPTCODE FROM ORAGMP.CMEMPM WHERE DEPTCODE = #{as_dept_cd} AND #{as_assgnCode} in ('27027','27030','27035') ) UNION ALL SELECT #{as_emp_cd} FROM DUAL WHERE #{as_assgnCode} IN ('27040','27050') )

/*거래처별 원장 목록*/
SELECT * FROM (SELECT T0.*, FLOOR((ROWNUM - 1) / #{perPageRow,jdbcType=INTEGER} + 1) PAGE FROM (
SELECT A.GB AS GB , --자료순서 A.APPDATE AS YMD , --거래일자 A.ECUSTNAME AS RCUST_NM , --간납처명 A.ITEMNAME AS ITEM_NM , --제품명 A.UNIT AS STANDARD , --규격 A.SALQTY AS QTY , --수량 A.SALPRC AS DANGA , --단가 A.SALAMT AS AMT , --공급가액 A.SALVAT AS VAT , --브가세 A.TOTAMT AS TOT , --합계 A.COLAMT AS SUKUM , --수금액 SUM(A.REMAMT) OVER(ORDER BY A.GB, A.SALDIV, A.ORDERNO, A.SEQ, A.ITEMCODE) AS REM_AMT , --잔고 0 AS DC_AMT , A.REMARK AS BIGO, --비고 B.BUSINESSNO AS VOU_NO, B.CEONAME AS PRESIDENT, NVL(B.CUSTEMPNAME,'정보없음') AS SAWON_NM, B.TELNO AS TEL
FROM ( SELECT '0' || TO_CHAR(TO_DATE(#{ad_fr_date},'YYYY-MM-DD') - 1,'YYYY-MM-DD') AS gb , --자료순서 TO_CHAR(TO_DATE(#{ad_fr_date},'YYYY-MM-DD') - 1,'YYYY-MM-DD') AS appdate , --거래일자 '0' AS orderno , --전표번호 0 AS seq , --거래순번 '' AS saldiv , --영업구분 '' AS saldivnm , --영업구분명 '' AS ecustcode, --간납처코드 '' AS ecustname, --간납처명 '' AS itemcode , --제품코드 '전기이월' AS itemname , --제품명 '' AS unit , --규격 0 AS salqty , --수량 0 AS salprc , --단가 0 AS salvat , --공급가액 0 AS salamt , --부가세 0 AS totamt , --합계 0 AS colamt , --수금액 '' AS orderdiv , --영업영역 '' AS ordername, --영업영역명 '' AS remark , --비고 SUM(A.REMAMT) AS remamt , --잔고산출용 0 AS turncnt , #{as_cust_id} AS CUSTCODE FROM ( --전월잔고 SELECT NVL(SUM(A.BALANCE),0) AS REMAMT FROM ORAGMP.SLRESULTM A WHERE A.YEARMONTH = TO_CHAR(TO_DATE(substr(#{ad_fr_date},1,7)||'-01','YYYY/MM/DD') - 1,'YYYY-MM') AND A.CUSTCODE = #{as_cust_id} UNION ALL --1일부터 시작전일까지의 판매 금액산출 SELECT NVL(SUM(CASE WHEN SUBSTR(A.SALDIV,1,1) = 'A' THEN B.TOTAMT ELSE -B.TOTAMT END),0) AS AMT FROM ORAGMP.SLORDM A, ORAGMP.SLORDD B WHERE A.STATEDIV = '09' AND A.ORDERNO = B.ORDERNO AND A.APPDATE >= substr(#{ad_fr_date},1,7)||'-01' AND A.APPDATE < TO_CHAR(TO_DATE(#{ad_fr_date},'YYYY-MM-DD') ,'YYYY-MM-DD') AND A.CUSTCODE = #{as_cust_id} UNION ALL --1일부터 시작전일까지의 수금 금액산출 SELECT NVL(SUM(-A.COLAMT),0) AS AMT FROM ORAGMP.SLCOLM A WHERE A.STATEDIV = '09' AND A.APPDATE >= substr(#{ad_fr_date},1,7)||'-01' AND A.APPDATE < TO_CHAR(TO_DATE(#{ad_fr_date},'YYYY-MM-DD') ,'YYYY-MM-DD') AND A.CUSTCODE = #{as_cust_id} ) A UNION ALL SELECT '1' || A.APPDATE AS gb , --자료순서 A.APPDATE AS appdate , --거래일자 A.ORDERNO AS orderno , --전표번호 B.SEQ AS seq , --거래순번 A.SALDIV AS saldiv , --영업구분 S.DIVNAME AS saldivnm , --영업구분명 A.ECUSTCODE AS ecustcode, --간납처코드 H.CUSTNAME AS ecustname, --간납처명 B.ITEMCODE AS itemcode , --제품코드 I.ITEMNAME AS itemname , --제품명 I.ITEMUNIT AS unit , --규격 CASE SUBSTR(A.SALDIV,1,1) WHEN 'A' THEN B.SALQTY ELSE -B.SALQTY END AS salqty , --수량 B.SALPRC AS salprc , --단가 CASE SUBSTR(A.SALDIV,1,1) WHEN 'A' THEN B.SALVAT ELSE -B.SALVAT END AS salvat , --부가세 CASE SUBSTR(A.SALDIV,1,1) WHEN 'A' THEN B.SALAMT ELSE -B.SALAMT END AS salamt , --공급가액 CASE SUBSTR(A.SALDIV,1,1) WHEN 'A' THEN B.TOTAMT ELSE -B.TOTAMT END AS totamt , --합계 0 AS colamt , --수금액 A.ORDERDIV AS orderdiv , --영업영역 T.DIVNAME AS ordername, --영업영역명 A.REMARK AS remark , --비고 CASE SUBSTR(A.SALDIV,1,1) WHEN 'A' THEN B.TOTAMT ELSE -B.TOTAMT END AS remamt , --잔고산출용 0 AS turncnt , #{as_cust_id} AS CUSTCODE FROM ORAGMP.SLORDM A, ORAGMP.SLORDD B, ORAGMP.CMCUSTM H, ORAGMP.CMITEMM I, ORAGMP.CMCOMMONM S, ORAGMP.CMCOMMONM T WHERE A.STATEDIV = '09' AND A.ORDERNO = B.ORDERNO AND A.ECUSTCODE = H.CUSTCODE AND 'SL10' = S.CMMCODE (+) AND A.SALDIV = S.DIVCODE (+) AND 'SL43' = T.CMMCODE (+) AND A.ORDERDIV = T.DIVCODE (+) AND B.ITEMCODE = I.ITEMCODE(+) AND A.APPDATE BETWEEN #{ad_fr_date} AND #{ad_to_date} AND A.CUSTCODE = #{as_cust_id} UNION ALL SELECT '1' || A.APPDATE AS gb , --자료순서 A.APPDATE AS appdate , --거래일자 A.COLNO AS orderno , --전표번호 TO_NUMBER(A.COLSEQ) AS seq , --거래순번 A.COLDIV AS saldiv , --영업구분 S.DIVNAME AS saldivnm , --영업구분명 A.ECUSTCODE AS ecustcode, --간납처코드 H.CUSTNAME AS ecustname, --간납처명 '' AS itemcode , --제품코드 CASE WHEN A.COLDIV LIKE '3%' THEN NVL(A.BILLNO, 'unknown') || ':' || A.EXPDATE --어음번호와 만기일자 WHEN SUBSTR(A.COLDIV, 1, 1) = '1' THEN I.ACCREMARK || ':' || NVL(A.ACCOUNTNO,'') --계좌은행 WHEN SUBSTR(A.COLDIV, 1, 1) = '2' THEN (CASE WHEN (NVL (A.DIVMONTH, 0) = 0) THEN U.DIVNAME --카드사 WHEN (NVL (A.DIVMONTH, 0) > 0) THEN U.DIVNAME || ' ' || TO_CHAR(A.DIVMONTH) || '개월' --카드사 ELSE '' END) ELSE '' END AS itemname , --제품명 '' AS unit , --규격 0 AS salqty , --수량 0 AS salprc , --단가 CASE WHEN A.COLDIV IN ('52','59') THEN -A.COLVAT ELSE 0 END AS salvat , --부가세 CASE WHEN A.COLDIV IN ('52','59') THEN -(A.COLAMT - A.COLVAT) ELSE 0 END AS salamt , --공급가액 CASE WHEN A.COLDIV IN ('52','59') THEN -A.COLAMT ELSE 0 END AS totamt , --합계 CASE WHEN A.COLDIV NOT IN ('52','59') THEN A.COLAMT ELSE 0 END AS colamt , --수금액 A.ORDERDIV AS orderdiv , --영업영역 T.DIVNAME AS ordername, --영업영역명 A.REMARK AS remark , --비고 -A.COLAMT AS remamt , --잔고산출용 0 AS turncnt , #{as_cust_id} AS CUSTCODE FROM ORAGMP.SLCOLM A, ORAGMP.CMCUSTM H, ORAGMP.CMACCOUNTM I, ORAGMP.CMCOMMONM S, ORAGMP.CMCOMMONM T, ORAGMP.CMCOMMONM U WHERE A.STATEDIV = '09' AND A.ECUSTCODE = H.CUSTCODE AND A.ACCOUNTNO = I.ACCOUNTNO(+) AND 'SL18' = S.CMMCODE (+) AND A.COLDIV = S.DIVCODE (+) AND 'SL43' = T.CMMCODE (+) AND A.ORDERDIV = T.DIVCODE (+) AND 'AC17' = U.CMMCODE (+) AND A.CARDCOMP = U.DIVCODE (+) AND A.APPDATE BETWEEN #{ad_fr_date} AND #{ad_to_date} AND A.CUSTCODE = #{as_cust_id} UNION ALL SELECT '1' || a.appdate || '-98-99' AS gb , --자료순서 '' AS appdate , --거래일자 '0' AS orderno , --전표번호 0 AS seq , --거래순번 'Z99' AS saldiv , --영업구분 '' AS saldivnm , --영업구분명 '' AS ecustcode, --간납처코드 '' AS ecustname, --간납처명 '' AS itemcode , --제품코드 REPLACE(A.appdate, '-', '/') || ' 월 계' AS itemname , --제품명 '' AS unit , --규격 0 AS salqty , --수량 0 AS salprc , --단가 SUM(A.SALVAT) AS salvat , --부가세 SUM(A.SALAMT) AS salamt , --공급가액 SUM(A.TOTAMT) AS totamt , --합계 SUM(A.COLAMT) AS colamt , --수금액 '' AS orderdiv , --영업영역 '' AS ordername, --영업영역명 '' AS remark , --비고 0 AS remamt , --잔고산출용 NVL(B.TURNCNT,0) AS turncnt , #{as_cust_id} AS CUSTCODE FROM ( SELECT A.CUSTCODE AS CUSTCODE , --거래처코드 SUBSTR(A.APPDATE,1,7) AS appdate , --거래일자 CASE SUBSTR(A.SALDIV,1,1) WHEN 'A' THEN B.SALVAT ELSE -B.SALVAT END AS SALVAT , --부가세 CASE SUBSTR(A.SALDIV,1,1) WHEN 'A' THEN B.SALAMT ELSE -B.SALAMT END AS SALAMT , --공급가액 CASE SUBSTR(A.SALDIV,1,1) WHEN 'A' THEN B.TOTAMT ELSE -B.TOTAMT END AS TOTAMT , --합계 0 AS COLAMT --수금액 FROM ORAGMP.SLORDM A, ORAGMP.SLORDD B WHERE A.STATEDIV = '09' AND A.ORDERNO = B.ORDERNO AND A.APPDATE BETWEEN #{ad_fr_date} AND #{ad_to_date} AND A.CUSTCODE = #{as_cust_id} UNION ALL SELECT A.CUSTCODE AS CUSTCODE , --거래처코드 SUBSTR(A.APPDATE,1,7) AS appdate , --거래일자 CASE WHEN A.COLDIV IN ('52','59') THEN -A.COLVAT ELSE 0 END AS salvat , --부가세 CASE WHEN A.COLDIV IN ('52','59') THEN -(A.COLAMT - A.COLVAT) ELSE 0 END AS salamt , --공급가액 CASE WHEN A.COLDIV IN ('52','59') THEN -A.COLAMT ELSE 0 END AS totamt , --합계 CASE WHEN A.COLDIV NOT IN ('52','59') THEN A.COLAMT ELSE 0 END AS colamt --수금액 FROM ORAGMP.SLCOLM A WHERE A.STATEDIV = '09' AND A.APPDATE BETWEEN #{ad_fr_date} AND #{ad_to_date} AND A.CUSTCODE = #{as_cust_id} ) A, ORAGMP.SLTURNCUSTM B WHERE A.appdate = B.YEARMONTH(+) AND A.CUSTCODE = B.CUSTCODE (+) GROUP BY A.appdate, B.TURNCNT UNION ALL SELECT '1' || a.appdate || '-99-99' AS gb , --자료순서 '' AS appdate , --거래일자 '0' AS orderno , --전표번호 0 AS seq , --거래순번 'Z99' AS saldiv , --영업구분 '' AS saldivnm , --영업구분명 '' AS ecustcode, --간납처코드 '' AS ecustname, --간납처명 '' AS itemcode , --제품코드 REPLACE(A.appdate, '-', '/') || ' 년 계' AS itemname , --제품명 '' AS unit , --규격 0 AS salqty , --수량 0 AS salprc , --단가 SUM(A.SALVAT) AS salvat , --부가세 SUM(A.SALAMT) AS salamt , --공급가액 SUM(A.TOTAMT) AS totamt , --합계 SUM(A.COLAMT) AS colamt , --수금액 '' AS orderdiv , --영업영역 '' AS ordername, --영업영역명 '' AS remark , --비고 0 AS remamt , --잔고산출용 0 AS turncnt , #{as_cust_id} AS CUSTCODE FROM ( SELECT SUBSTR(A.APPDATE,1,4) AS appdate , --거래일자 CASE SUBSTR(A.SALDIV,1,1) WHEN 'A' THEN B.SALVAT ELSE -B.SALVAT END AS SALVAT , --부가세 CASE SUBSTR(A.SALDIV,1,1) WHEN 'A' THEN B.SALAMT ELSE -B.SALAMT END AS SALAMT , --공급가액 CASE SUBSTR(A.SALDIV,1,1) WHEN 'A' THEN B.TOTAMT ELSE -B.TOTAMT END AS TOTAMT , --합계 0 AS COLAMT --수금액 FROM ORAGMP.SLORDM A, ORAGMP.SLORDD B WHERE A.STATEDIV = '09' AND A.ORDERNO = B.ORDERNO AND A.APPDATE BETWEEN #{ad_fr_date} AND #{ad_to_date} AND A.CUSTCODE = #{as_cust_id} UNION ALL SELECT SUBSTR(A.APPDATE,1,4) AS appdate , --거래일자 CASE WHEN A.COLDIV IN ('52','59') THEN -A.COLVAT ELSE 0 END AS salvat , --부가세 CASE WHEN A.COLDIV IN ('52','59') THEN -(A.COLAMT - A.COLVAT) ELSE 0 END AS salamt , --공급가액 CASE WHEN A.COLDIV IN ('52','59') THEN -A.COLAMT ELSE 0 END AS totamt , --합계 CASE WHEN A.COLDIV NOT IN ('52','59') THEN A.COLAMT ELSE 0 END AS colamt --수금액 FROM ORAGMP.SLCOLM A WHERE A.STATEDIV = '09' AND A.APPDATE BETWEEN #{ad_fr_date} AND #{ad_to_date} AND A.CUSTCODE = #{as_cust_id} ) A GROUP BY A.appdate ) A,ORAGMP.CMCUSTM B WHERE A.CUSTCODE = B.CUSTCODE ORDER BY A.GB, A.SALDIV, A.ORDERNO, A.SEQ, A.ITEMCODE
ORDER BY ${sidx} ${sord}
) T0 ) WHERE PAGE = #{page,jdbcType=INTEGER}

/*간납처별 원장목록 하단 Grid 목록*/
SELECT * FROM (SELECT T0.*, FLOOR((ROWNUM - 1) / #{perPageRow,jdbcType=INTEGER} + 1) PAGE FROM (
SELECT A.GB AS GB , --자료순서 A.APPDATE AS YMD , --거래일자 A.CUSTNAME AS RCUST_NM , --거래처명 A.ITEMNAME AS ITEM_NM , --제품명 A.UNIT AS STANDARD , --규격 A.SALQTY AS QTY , --수량 A.SALPRC AS DANGA , --단가 A.SALAMT AS AMT , --공급가액 A.SALVAT AS VAT , --브가세 A.TOTAMT AS TOT , --합계 A.COLAMT AS SUKUM , --수금액 SUM(A.REMAMT) OVER(ORDER BY A.GB, A.SALDIV, A.ORDERNO, A.SEQ, A.ITEMCODE) AS REM_AMT , --잔고 0 AS DC_AMT , A.REMARK AS BIGO, --비고 B.BUSINESSNO AS VOU_NO, B.CEONAME AS PRESIDENT, NVL(B.CUSTEMPNAME,'정보없음') AS SAWON_NM, B.TELNO AS TEL
FROM ( SELECT '0' || TO_CHAR(TO_DATE(#{ad_fr_date},'YYYY-MM-DD') - 1,'YYYY-MM-DD') AS gb , --자료순서 TO_CHAR(TO_DATE(#{ad_fr_date},'YYYY-MM-DD') - 1,'YYYY-MM-DD') AS appdate , --거래일자 '0' AS orderno , --전표번호 0 AS seq , --거래순번 '' AS saldiv , --영업구분 '' AS saldivnm , --영업구분명 '' AS custcode, --거래처코드 '' AS custname, --거래처명 '' AS itemcode , --제품코드 '전기이월' AS itemname , --제품명 '' AS unit , --규격 0 AS salqty , --수량 0 AS salprc , --단가 0 AS salvat , --공급가액 0 AS salamt , --부가세 0 AS totamt , --합계 0 AS colamt , --수금액 '' AS orderdiv , --영업영역 '' AS ordername, --영업영역명 '' AS remark , --비고 SUM(A.REMAMT) AS remamt , --잔고산출용 0 AS turncnt FROM ( --전월잔고 SELECT NVL(SUM(A.BALANCE),0) AS REMAMT FROM ORAGMP.SLRESULTM A WHERE A.YEARMONTH = TO_CHAR(TO_DATE(substr(#{ad_fr_date},1,7)||'-01','YYYY/MM/DD') - 1,'YYYY-MM') AND A.ECUSTCODE = #{as_cust_id} UNION ALL --1일부터 시작전일까지의 판매 금액산출 SELECT NVL(SUM(CASE WHEN SUBSTR(A.SALDIV,1,1) = 'A' THEN B.TOTAMT ELSE -B.TOTAMT END),0) AS AMT FROM ORAGMP.SLORDM A, ORAGMP.SLORDD B WHERE A.STATEDIV = '09' AND A.ORDERNO = B.ORDERNO AND A.APPDATE >= substr(#{ad_fr_date},1,7)||'-01' AND A.APPDATE < TO_CHAR(TO_DATE(#{ad_fr_date},'YYYY-MM-DD') ,'YYYY-MM-DD') AND A.ECUSTCODE = #{as_cust_id} UNION ALL --1일부터 시작전일까지의 수금 금액산출 SELECT NVL(SUM(-A.COLAMT),0) AS AMT FROM ORAGMP.SLCOLM A WHERE A.STATEDIV = '09' AND A.APPDATE >= substr(#{ad_fr_date},1,7)||'-01' AND A.APPDATE < TO_CHAR(TO_DATE(#{ad_fr_date},'YYYY-MM-DD') ,'YYYY-MM-DD') AND A.ECUSTCODE = #{as_cust_id} ) A UNION ALL SELECT '1' || A.APPDATE AS gb , --자료순서 A.APPDATE AS appdate , --거래일자 A.ORDERNO AS orderno , --전표번호 B.SEQ AS seq , --거래순번 A.SALDIV AS saldiv , --영업구분 S.DIVNAME AS saldivnm , --영업구분명 A.CUSTCODE AS custcode, --거래처코드 H.CUSTNAME AS custname, --거래처명 B.ITEMCODE AS itemcode , --제품코드 I.ITEMNAME AS itemname , --제품명 I.ITEMUNIT AS unit , --규격 CASE SUBSTR(A.SALDIV,1,1) WHEN 'A' THEN B.SALQTY ELSE -B.SALQTY END AS salqty , --수량 B.SALPRC AS salprc , --단가 CASE SUBSTR(A.SALDIV,1,1) WHEN 'A' THEN B.SALVAT ELSE -B.SALVAT END AS salvat , --부가세 CASE SUBSTR(A.SALDIV,1,1) WHEN 'A' THEN B.SALAMT ELSE -B.SALAMT END AS salamt , --공급가액 CASE SUBSTR(A.SALDIV,1,1) WHEN 'A' THEN B.TOTAMT ELSE -B.TOTAMT END AS totamt , --합계 0 AS colamt , --수금액 A.ORDERDIV AS orderdiv , --영업영역 T.DIVNAME AS ordername, --영업영역명 A.REMARK AS remark , --비고 CASE SUBSTR(A.SALDIV,1,1) WHEN 'A' THEN B.TOTAMT ELSE -B.TOTAMT END AS remamt , --잔고산출용 0 AS turncnt FROM ORAGMP.SLORDM A, ORAGMP.SLORDD B, ORAGMP.CMCUSTM H, ORAGMP.CMITEMM I, ORAGMP.CMCOMMONM S, ORAGMP.CMCOMMONM T WHERE A.STATEDIV = '09' AND A.ORDERNO = B.ORDERNO AND A.CUSTCODE = H.CUSTCODE AND 'SL10' = S.CMMCODE (+) AND A.SALDIV = S.DIVCODE (+) AND 'SL43' = T.CMMCODE (+) AND A.ORDERDIV = T.DIVCODE (+) AND B.ITEMCODE = I.ITEMCODE(+) AND A.APPDATE BETWEEN #{ad_fr_date} AND #{ad_to_date} AND A.ECUSTCODE = #{as_cust_id} UNION ALL SELECT '1' || A.APPDATE AS gb , --자료순서 A.APPDATE AS appdate , --거래일자 A.COLNO AS orderno , --전표번호 TO_NUMBER(A.COLSEQ) AS seq , --거래순번 A.COLDIV AS saldiv , --영업구분 S.DIVNAME AS saldivnm , --영업구분명 A.ECUSTCODE AS ecustcode, --거래처코드 H.CUSTNAME AS ecustname, --거래처명 '' AS itemcode , --제품코드 CASE WHEN A.COLDIV LIKE '3%' THEN NVL(A.BILLNO, 'unknown') || ':' || A.EXPDATE --어음번호와 만기일자 WHEN SUBSTR(A.COLDIV, 1, 1) = '1' THEN I.ACCREMARK || ':' || NVL(A.ACCOUNTNO,'') --계좌은행 WHEN SUBSTR(A.COLDIV, 1, 1) = '2' THEN (CASE WHEN (NVL (A.DIVMONTH, 0) = 0) THEN U.DIVNAME --카드사 WHEN (NVL (A.DIVMONTH, 0) > 0) THEN U.DIVNAME || ' ' || TO_CHAR(A.DIVMONTH) || '개월' --카드사 ELSE '' END) ELSE '' END AS itemname , --제품명 '' AS unit , --규격 0 AS salqty , --수량 0 AS salprc , --단가 CASE WHEN A.COLDIV IN ('52','59') THEN -A.COLVAT ELSE 0 END AS salvat , --부가세 CASE WHEN A.COLDIV IN ('52','59') THEN -(A.COLAMT - A.COLVAT) ELSE 0 END AS salamt , --공급가액 CASE WHEN A.COLDIV IN ('52','59') THEN -A.COLAMT ELSE 0 END AS totamt , --합계 CASE WHEN A.COLDIV NOT IN ('52','59') THEN A.COLAMT ELSE 0 END AS colamt , --수금액 A.ORDERDIV AS orderdiv , --영업영역 T.DIVNAME AS ordername, --영업영역명 A.REMARK AS remark , --비고 -A.COLAMT AS remamt , --잔고산출용 0 AS turncnt FROM ORAGMP.SLCOLM A, ORAGMP.CMCUSTM H, ORAGMP.CMACCOUNTM I, ORAGMP.CMCOMMONM S, ORAGMP.CMCOMMONM T, ORAGMP.CMCOMMONM U WHERE A.STATEDIV = '09' AND A.ECUSTCODE = H.CUSTCODE AND A.ACCOUNTNO = I.ACCOUNTNO(+) AND 'SL18' = S.CMMCODE (+) AND A.COLDIV = S.DIVCODE (+) AND 'SL43' = T.CMMCODE (+) AND A.ORDERDIV = T.DIVCODE (+) AND 'AC17' = U.CMMCODE (+) AND A.CARDCOMP = U.DIVCODE (+) AND A.APPDATE BETWEEN #{ad_fr_date} AND #{ad_to_date} AND A.CUSTCODE = #{as_cust_id} UNION ALL SELECT '1' || a.appdate || '-98-99' AS gb , --자료순서 '' AS appdate , --거래일자 '0' AS orderno , --전표번호 0 AS seq , --거래순번 'Z99' AS saldiv , --영업구분 '' AS saldivnm , --영업구분명 '' AS custcode, --거래처코드 '' AS custname, --거래처명 '' AS itemcode , --제품코드 REPLACE(A.appdate, '-', '/') || ' 월 계' AS itemname , --제품명 '' AS unit , --규격 0 AS salqty , --수량 0 AS salprc , --단가 SUM(A.SALVAT) AS salvat , --부가세 SUM(A.SALAMT) AS salamt , --공급가액 SUM(A.TOTAMT) AS totamt , --합계 SUM(A.COLAMT) AS colamt , --수금액 '' AS orderdiv , --영업영역 '' AS ordername, --영업영역명 '' AS remark , --비고 0 AS remamt , --잔고산출용 NVL(B.TURNCNT,0) AS turncnt FROM ( SELECT A.ECUSTCODE AS ECUSTCODE , --거래처코드 SUBSTR(A.APPDATE,1,7) AS appdate , --거래일자 CASE SUBSTR(A.SALDIV,1,1) WHEN 'A' THEN B.SALVAT ELSE -B.SALVAT END AS SALVAT , --부가세 CASE SUBSTR(A.SALDIV,1,1) WHEN 'A' THEN B.SALAMT ELSE -B.SALAMT END AS SALAMT , --공급가액 CASE SUBSTR(A.SALDIV,1,1) WHEN 'A' THEN B.TOTAMT ELSE -B.TOTAMT END AS TOTAMT , --합계 0 AS COLAMT --수금액 FROM ORAGMP.SLORDM A, ORAGMP.SLORDD B WHERE A.STATEDIV = '09' AND A.ORDERNO = B.ORDERNO AND A.APPDATE BETWEEN #{ad_fr_date} AND #{ad_to_date} AND A.ECUSTCODE = #{as_cust_id} UNION ALL SELECT A.ECUSTCODE AS ECUSTCODE , --거래처코드 SUBSTR(A.APPDATE,1,7) AS appdate , --거래일자 CASE WHEN A.COLDIV IN ('52','59') THEN -A.COLVAT ELSE 0 END AS salvat , --부가세 CASE WHEN A.COLDIV IN ('52','59') THEN -(A.COLAMT - A.COLVAT) ELSE 0 END AS salamt , --공급가액 CASE WHEN A.COLDIV IN ('52','59') THEN -A.COLAMT ELSE 0 END AS totamt , --합계 CASE WHEN A.COLDIV NOT IN ('52','59') THEN A.COLAMT ELSE 0 END AS colamt --수금액 FROM ORAGMP.SLCOLM A WHERE A.STATEDIV = '09' AND A.APPDATE BETWEEN #{ad_fr_date} AND #{ad_to_date} AND A.ECUSTCODE = #{as_cust_id} ) A, ORAGMP.SLTURNCUSTM B WHERE A.appdate = B.YEARMONTH(+) AND A.ECUSTCODE = B.CUSTCODE (+) GROUP BY A.appdate, B.TURNCNT UNION ALL SELECT '1' || a.appdate || '-99-99' AS gb , --자료순서 '' AS appdate , --거래일자 '0' AS orderno , --전표번호 0 AS seq , --거래순번 'Z99' AS saldiv , --영업구분 '' AS saldivnm , --영업구분명 '' AS custcode, --거래처코드 '' AS custname, --거래처명 '' AS itemcode , --제품코드 REPLACE(A.appdate, '-', '/') || ' 년 계' AS itemname , --제품명 '' AS unit , --규격 0 AS salqty , --수량 0 AS salprc , --단가 SUM(A.SALVAT) AS salvat , --부가세 SUM(A.SALAMT) AS salamt , --공급가액 SUM(A.TOTAMT) AS totamt , --합계 SUM(A.COLAMT) AS colamt , --수금액 '' AS orderdiv , --영업영역 '' AS ordername, --영업영역명 '' AS remark , --비고 0 AS remamt , --잔고산출용 0 AS turncnt FROM ( SELECT SUBSTR(A.APPDATE,1,4) AS appdate , --거래일자 CASE SUBSTR(A.SALDIV,1,1) WHEN 'A' THEN B.SALVAT ELSE -B.SALVAT END AS SALVAT , --부가세 CASE SUBSTR(A.SALDIV,1,1) WHEN 'A' THEN B.SALAMT ELSE -B.SALAMT END AS SALAMT , --공급가액 CASE SUBSTR(A.SALDIV,1,1) WHEN 'A' THEN B.TOTAMT ELSE -B.TOTAMT END AS TOTAMT , --합계 0 AS COLAMT --수금액 FROM ORAGMP.SLORDM A, ORAGMP.SLORDD B WHERE A.STATEDIV = '09' AND A.ORDERNO = B.ORDERNO AND A.APPDATE BETWEEN #{ad_fr_date} AND #{ad_to_date} AND A.ECUSTCODE = #{as_cust_id} UNION ALL SELECT SUBSTR(A.APPDATE,1,4) AS appdate , --거래일자 CASE WHEN A.COLDIV IN ('52','59') THEN -A.COLVAT ELSE 0 END AS salvat , --부가세 CASE WHEN A.COLDIV IN ('52','59') THEN -(A.COLAMT - A.COLVAT) ELSE 0 END AS salamt , --공급가액 CASE WHEN A.COLDIV IN ('52','59') THEN -A.COLAMT ELSE 0 END AS totamt , --합계 CASE WHEN A.COLDIV NOT IN ('52','59') THEN A.COLAMT ELSE 0 END AS colamt --수금액 FROM ORAGMP.SLCOLM A WHERE A.STATEDIV = '09' AND A.APPDATE BETWEEN #{ad_fr_date} AND #{ad_to_date} AND A.ECUSTCODE = #{as_cust_id} ) A GROUP BY A.appdate ) A ORDER BY A.GB, A.SALDIV, A.ORDERNO, A.SEQ, A.ITEMCODE
ORDER BY ${sidx} ${sord}
) T0 ) WHERE PAGE = #{page,jdbcType=INTEGER}

