 H.CUSTNAME AS custname, --거래처명 B.ITEMCODE AS itemcode , --제품코드 I.ITEMNAME AS itemname , --제품명 I.ITEMUNIT AS unit , --규격 CASE SUBSTR(A.SALDIV,1,1) WHEN 'A' THEN B.SALQTY ELSE -B.SALQTY END AS salqty , --수량 B.SALPRC AS salprc , --단가 CASE SUBSTR(A.SALDIV,1,1) WHEN 'A' THEN B.SALVAT ELSE -B.SALVAT END AS salvat , --부가세 CASE SUBSTR(A.SALDIV,1,1) WHEN 'A' THEN B.SALAMT ELSE -B.SALAMT END AS salamt , --공급가액 CASE SUBSTR(A.SALDIV,1,1) WHEN 'A' THEN B.TOTAMT ELSE -B.TOTAMT END AS totamt , --합계 0 AS colamt , --수금액 A.ORDERDIV AS orderdiv , --영업영역 T.DIVNAME AS ordername, --영업영역명 A.REMARK AS remark , --비고 CASE SUBSTR(A.SALDIV,1,1) WHEN 'A' THEN B.TOTAMT ELSE -B.TOTAMT END AS remamt , --잔고산출용 0 AS turncnt FROM ORAGMP.SLORDM A, ORAGMP.SLORDD B, ORAGMP.CMCUSTM H, ORAGMP.CMITEMM I, ORAGMP.CMCOMMONM S, ORAGMP.CMCOMMONM T WHERE A.STATEDIV = '09' AND A.ORDERNO = B.ORDERNO AND A.CUSTCODE = H.CUSTCODE AND 'SL10' = S.CMMCODE (+) AND A.SALDIV = S.DIVCODE (+) AND 'SL43' = T.CMMCODE (+) AND A.ORDERDIV = T.DIVCODE (+) AND B.ITEMCODE = I.ITEMCODE(+) AND A.APPDATE BETWEEN #{ad_fr_date} AND #{ad_to_date} AND A.ECUSTCODE = #{as_cust_id} UNION ALL SELECT '1' || A.APPDATE AS gb , --자료순서 A.APPDATE AS appdate , --거래일자 A.COLNO AS orderno , --전표번호 TO_NUMBER(A.COLSEQ) AS seq , --거래순번 A.COLDIV AS saldiv , --영업구분 S.DIVNAME AS saldivnm , --영업구분명 A.ECUSTCODE AS ecustcode, --거래처코드 H.CUSTNAME AS ecustname, --거래처명 '' AS itemcode , --제품코드 CASE WHEN A.COLDIV LIKE '3%' THEN NVL(A.BILLNO, 'unknown') || ':' || A.EXPDATE --어음번호와 만기일자 WHEN SUBSTR(A.COLDIV, 1, 1) = '1' THEN I.ACCREMARK || ':' || NVL(A.ACCOUNTNO,'') --계좌은행 WHEN SUBSTR(A.COLDIV, 1, 1) = '2' THEN (CASE WHEN (NVL (A.DIVMONTH, 0) = 0) THEN U.DIVNAME --카드사 WHEN (NVL (A.DIVMONTH, 0) > 0) THEN U.DIVNAME || ' ' || TO_CHAR(A.DIVMONTH) || '개월' --카드사 ELSE '' END) ELSE '' END AS itemname , --제품명 '' AS unit , --규격 0 AS salqty , --수량 0 AS salprc , --단가 CASE WHEN A.COLDIV IN ('52','59') THEN -A.COLVAT ELSE 0 END AS salvat , --부가세 CASE WHEN A.COLDIV IN ('52','59') THEN -(A.COLAMT - A.COLVAT) ELSE 0 END AS salamt , --공급가액 CASE WHEN A.COLDIV IN ('52','59') THEN -A.COLAMT ELSE 0 END AS totamt , --합계 CASE WHEN A.COLDIV NOT IN ('52','59') THEN A.COLAMT ELSE 0 END AS colamt , --수금액 A.ORDERDIV AS orderdiv , --영업영역 T.DIVNAME AS ordername, --영업영역명 A.REMARK AS remark , --비고 -A.COLAMT AS remamt , --잔고산출용 0 AS turncnt FROM ORAGMP.SLCOLM A, ORAGMP.CMCUSTM H, ORAGMP.CMACCOUNTM I, ORAGMP.CMCOMMONM S, ORAGMP.CMCOMMONM T, ORAGMP.CMCOMMONM U WHERE A.STATEDIV = '09' AND A.ECUSTCODE = H.CUSTCODE AND A.ACCOUNTNO = I.ACCOUNTNO(+) AND 'SL18' = S.CMMCODE (+) AND A.COLDIV = S.DIVCODE (+) AND 'SL43' = T.CMMCODE (+) AND A.ORDERDIV = T.DIVCODE (+) AND 'AC17' = U.CMMCODE (+) AND A.CARDCOMP = U.DIVCODE (+) AND A.APPDATE BETWEEN #{ad_fr_date} AND #{ad_to_date} AND A.CUSTCODE = #{as_cust_id} UNION ALL SELECT '1' || a.appdate || '-98-99' AS gb , --자료순서 '' AS appdate , --거래일자 '0' AS orderno , --전표번호 0 AS seq , --거래순번 'Z99' AS saldiv , --영업구분 '' AS saldivnm , --영업구분명 '' AS custcode, --거래처코드 '' AS custname, --거래처명 '' AS itemcode , --제품코드 REPLACE(A.appdate, '-', '/') || ' 월 계' AS itemname , --제품명 '' AS unit , --규격 0 AS salqty , --수량 0 AS salprc , --단가 SUM(A.SALVAT) AS salvat , --부가세 SUM(A.SALAMT) AS salamt , --공급가액 SUM(A.TOTAMT) AS totamt , --합계 SUM(A.COLAMT) AS colamt , --수금액 '' AS orderdiv , --영업영역 '' AS ordername, --영업영역명 '' AS remark , --비고 0 AS remamt , --잔고산출용 NVL(B.TURNCNT,0) AS turncnt FROM ( SELECT A.ECUSTCODE AS ECUSTCODE , --거래처코드 SUBSTR(A.APPDATE,1,7) AS appdate , --거래일자 CASE SUBSTR(A.SALDIV,1,1) WHEN 'A' THEN B.SALVAT ELSE -B.SALVAT END AS SALVAT , --부가세 CASE SUBSTR(A.SALDIV,1,1) WHEN 'A' THEN B.SALAMT ELSE -B.SALAMT END AS SALAMT , --공급가액 CASE SUBSTR(A.SALDIV,1,1) WHEN 'A' THEN B.TOTAMT ELSE -B.TOTAMT END AS TOTAMT , --합계 0 AS COLAMT --수금액 FROM ORAGMP.SLORDM A, ORAGMP.SLORDD B WHERE A.STATEDIV = '09' AND A.ORDERNO = B.ORDERNO AND A.APPDATE BETWEEN #{ad_fr_date} AND #{ad_to_date} AND A.ECUSTCODE = #{as_cust_id} UNION ALL SELECT A.ECUSTCODE AS ECUSTCODE , --거래처코드 SUBSTR(A.APPDATE,1,7) AS appdate , --거래일자 CASE WHEN A.COLDIV IN ('52','59') THEN -A.COLVAT ELSE 0 END AS salvat , --부가세 CASE WHEN A.COLDIV IN ('52','59') THEN -(A.COLAMT - A.COLVAT) ELSE 0 END AS salamt , --공급가액 CASE WHEN A.COLDIV IN ('52','59') THEN -A.COLAMT ELSE 0 END AS totamt , --합계 CASE WHEN A.COLDIV NOT IN ('52','59') THEN A.COLAMT ELSE 0 END AS colamt --수금액 FROM ORAGMP.SLCOLM A WHERE A.STATEDIV = '09' AND A.APPDATE BETWEEN #{ad_fr_date} AND #{ad_to_date} AND A.ECUSTCODE = #{as_cust_id} ) A, ORAGMP.SLTURNCUSTM B WHERE A.appdate = B.YEARMONTH(+) AND A.ECUSTCODE = B.CUSTCODE (+) GROUP BY A.appdate, B.TURNCNT
