UNION ALL SELECT '1' || a.appdate || '-99-99' AS gb , --자료순서 '' AS appdate , --거래일자 '0' AS orderno , --전표번호 0 AS seq , --거래순번 'Z99' AS saldiv , --영업구분 '' AS saldivnm , --영업구분명 '' AS custcode, --거래처코드 '' AS custname, --거래처명 '' AS itemcode , --제품코드 REPLACE(A.appdate, '-', '/') || ' 년 계' AS itemname , --제품명 '' AS unit , --규격 0 AS salqty , --수량 0 AS salprc , --단가 SUM(A.SALVAT) AS salvat , --부가세 SUM(A.SALAMT) AS salamt , --공급가액 SUM(A.TOTAMT) AS totamt , --합계 SUM(A.COLAMT) AS colamt , --수금액 '' AS orderdiv , --영업영역 '' AS ordername, --영업영역명 '' AS remark , --비고 0 AS remamt , --잔고산출용 0 AS turncnt FROM ( SELECT SUBSTR(A.APPDATE,1,4) AS appdate , --거래일자 CASE SUBSTR(A.SALDIV,1,1) WHEN 'A' THEN B.SALVAT ELSE -B.SALVAT END AS SALVAT , --부가세 CASE SUBSTR(A.SALDIV,1,1) WHEN 'A' THEN B.SALAMT ELSE -B.SALAMT END AS SALAMT , --공급가액 CASE SUBSTR(A.SALDIV,1,1) WHEN 'A' THEN B.TOTAMT ELSE -B.TOTAMT END AS TOTAMT , --합계 0 AS COLAMT --수금액 FROM ORAGMP.SLORDM A, ORAGMP.SLORDD B WHERE A.STATEDIV = '09' AND A.ORDERNO = B.ORDERNO AND A.APPDATE BETWEEN #{ad_fr_date} AND #{ad_to_date} AND A.ECUSTCODE = #{as_cust_id} UNION ALL SELECT SUBSTR(A.APPDATE,1,4) AS appdate , --거래일자 CASE WHEN A.COLDIV IN ('52','59') THEN -A.COLVAT ELSE 0 END AS salvat , --부가세 CASE WHEN A.COLDIV IN ('52','59') THEN -(A.COLAMT - A.COLVAT) ELSE 0 END AS salamt , --공급가액 CASE WHEN A.COLDIV IN ('52','59') THEN -A.COLAMT ELSE 0 END AS totamt , --합계 CASE WHEN A.COLDIV NOT IN ('52','59') THEN A.COLAMT ELSE 0 END AS colamt --수금액 FROM ORAGMP.SLCOLM A WHERE A.STATEDIV = '09' AND A.APPDATE BETWEEN #{ad_fr_date} AND #{ad_to_date} AND A.ECUSTCODE = #{as_cust_id} ) A GROUP BY A.appdate ) A
