mapper.select:
SELECT * FROM (SELECT T0.*, FLOOR((ROWNUM - 1) / #{perPageRow,jdbcType=INTEGER} + 1) PAGE FROM (
SELECT COM.VOU_NO AS VOU_NO, BAS.ENCMPY_DT,
CASE WHEN TO_DATE (BAS.ENCMPY_DT) < TO_DATE (OUTF.YEAR || '0101') THEN OUTF.YEAR || '0101' ELSE BAS.ENCMPY_DT
END BAS_START_DT, DECODE(BAS.RETIR_DT ,'' ,OUTF.YEAR||'1231' ,BAS.RETIR_DT) AS BAS_END_DT, (NVL (WITHH.PAY_SUM, 0) - NVL (WITHH.FREE_TAX_SUM, 0)) AS PAY_SUM, WITHH.BONUS_SUM AS BONUS_SUM, WITHH.FREE_OVERTIME_SUM AS FREE_OVERTIME_SUM, WITHH.FREE_TAX_SUM AS FREE_TAX_SUM, OUTF.DETERMINE_INCOME_TAX AS DETERMINE_INCOME_TAX, OUTF.DETERMINE_JUMIN_TAX AS DETERMINE_JUMIN_TAX, OUTF.DETERMINE_NONG_TAX AS DETERMINE_NONG_TAX, OUTF.DEDUCTION_INCOME_TAX AS DEDUCTION_INCOME_TAX, OUTF.DEDUCTION_JUMIN_TAX AS DEDUCTION_JUMIN_TAX, OUTF.DEDUCTION_NONG_TAX AS DEDUCTION_NONG_TAX, OUTF.ADJST_DIV AS ADJST_DIV, OUTF.YEAR AS YEAR, INF.PAY_SUM AS PAY_SUM_TOTAL, INF.KUK_YEON_AMT AS KUK_YEON_AMT, INF.INSURANCE_HEALTH_AMT AS INSURANCE_HEALTH_AMT, INF.INSURANCE_EMPLOY_AMT AS INSURANCE_EMPLOY_AMT, INF.JING_INCOME_TAX AS JING_INCOME_TAX, INF.JING_JUMIN_TAX AS JING_JUMIN_TAX, INF.JING_NONG_TAX AS JING_NONG_TAX, INF.JING_JONG_INCOME_TAX AS JING_JONG_INCOME_TAX, INF.JING_JONG_JUMIN_TAX AS JING_JONG_JUMIN_TAX, INF.JING_JONG_NONG_TAX AS JING_JONG_NONG_TAX, FAMILY.FOREIGN_CD AS FOREIGN_CD FROM HANAHR.HR_YT_OUT_FNDATA_1 OUTF, HANAHR.HR_YT_IN_FNDATA_0 INF, HANAHR.HR_YT_IN_FAMILY_0 FAMILY, (SELECT YEAR, ADJST_DIV, EMP_NO, SUM (PAY_SUM) AS PAY_SUM, SUM (BONUS_SUM) AS BONUS_SUM, SUM (FREE_OVERTIME_SUM) AS FREE_OVERTIME_SUM, SUM (FREE_TAX_SUM) AS FREE_TAX_SUM FROM HANAHR.HR_YT_OUT_WITHHOLDING_0 GROUP BY YEAR, ADJST_DIV, EMP_NO) WITHH, HANAHR.HR_YT_BAS_COMPANY_0 COM, HANAHR.HR_HC_EMPBAS_0 BAS, (SELECT * FROM HANAHR.HR_YT_IN_PREVIOUS_0 WHERE SEQ = 1) PREV, (SELECT * FROM HANAHR.HR_YT_IN_PREVIOUS_0 WHERE SEQ = 2) PREV2 WHERE OUTF.EMP_NO = #{emp_no} AND OUTF.YEAR = INF.YEAR AND OUTF.ADJST_DIV = INF.ADJST_DIV AND OUTF.EMP_NO = INF.EMP_NO AND OUTF.YEAR = WITHH.YEAR AND OUTF.ADJST_DIV = WITHH.ADJST_DIV AND OUTF.EMP_NO = WITHH.EMP_NO AND OUTF.EMP_NO = BAS.EMP_NO AND OUTF.YEAR = PREV.YEAR(+) AND OUTF.ADJST_DIV = PREV.ADJST_DIV(+) AND OUTF.EMP_NO = PREV.EMP_NO(+) AND OUTF.YEAR = PREV2.YEAR(+) AND OUTF.ADJST_DIV = PREV2.ADJST_DIV(+) AND OUTF.EMP_NO = PREV2.EMP_NO(+) AND OUTF.YEAR = FAMILY.YEAR AND OUTF.ADJST_DIV = FAMILY.ADJST_DIV AND OUTF.EMP_NO = FAMILY.EMP_NO AND FAMILY.REL_CD = '0'
) T0 ) WHERE PAGE = #{page,jdbcType=INTEGER}

SELECT A.YEAR AS YEAR ,A.ADJST_DIV AS ADJST_DIV ,A.EMP_NO AS EMP_NO ,A.SEQ AS SEQ ,A.HOUSE_GB AS HOUSE_GB ,A.HOUSE_NM AS HOUSE_NM ,A.HOUSE_JUMIN AS HOUSE_JUMIN ,A.HOUSE_START_DT AS HOUSE_START_DT ,A.HOUSE_END_DT AS HOUSE_END_DT ,A.HOUSE_AMT1 AS HOUSE_AMT1 ,A.HOUSE_AMT2 AS HOUSE_AMT2 ,A.HOUSE_AMT3 AS HOUSE_AMT3 ,A.HOUSE_AMT4 AS HOUSE_AMT4 ,A.HOUSE_AMT5 AS HOUSE_AMT5 ,A.HOUSE_ADDR AS HOUSE_ADDR ,A.FIRST_EMP_NO ,A.FIRST_REGDATE ,A.LAST_EMP_NO ,A.LAST_REGDATE ,A.LAST_IP FROM HANAHR.HR_YT_IN_HOUSE_0 A WHERE A.YEAR = #{year} AND A.ADJST_DIV = #{adjst_div} AND A.EMP_NO = #{emp_no}
AND A.SEQ IN ('2', '3')
AND A.SEQ = '1'

SELECT BANK_CD, BANK_NM
  FROM HANAHR.HR_YT_BAS_BANKCD_0
 WHERE YEAR = #{year}
   AND ADJST_DIV = #{adjst_div}

   AND BANK_CD LIKE '%'||#{searchCode}||'%'


   AND BANK_NM LIKE '%'||#{searchName}||'%'

		SELECT A.FAMILY_OLD2_CNT AS FAMILY_OLD2_CNT                  
           ,A.FAMILY_DISABLED_PERSON_CNT AS FAMILY_DISABLED_PERSON_CNT       
           ,A.FAMILY_WOMEN_AMT AS FAMILY_WOMEN_AMT         
           ,A.FAMILY_SIXYEAR_CNT AS FAMILY_SIXYEAR_CNT                 
           ,A.FAMILY_BIRTH_CNT AS FAMILY_BIRTH_CNT
           ,A.FAMILY_SINGLE_PARENT_AMT AS FAMILY_SINGLE_PARENT_AMT
		FROM HANAHR.HR_YT_IN_FNDATA_0 A       
		WHERE A.YEAR = #{year}
		AND A.ADJST_DIV = #{adjst_div}
		AND A.EMP_NO = #{emp_no}

		SELECT A.KUK_YEON_AMT AS KUK_YEON_AMT  
           ,A.ANNUITY_SAVNG_AMT AS ANNUITY_SAVNG_AMT            	     
		FROM HANAHR.HR_YT_IN_FNDATA_0 A        
		WHERE A.YEAR = #{year}
		AND A.ADJST_DIV = #{adjst_div}
		AND A.EMP_NO = #{emp_no}

		SELECT A.INSURANCE_HEALTH_AMT AS INSURANCE_HEALTH_AMT    
           ,A.INSURANCE_EMPLOY_AMT AS INSURANCE_EMPLOY_AMT
           ,A.INSURANCE_SECRITY_AMT AS INSURANCE_SECRITY_AMT
           ,A.INSURANCE_DISABLED_PERSON_AMT AS INSURANCE_DISABLED_PERSON_AMT                       
		FROM HANAHR.HR_YT_IN_FNDATA_0 A   
		WHERE A.YEAR = #{year}
		AND A.ADJST_DIV = #{adjst_div}
		AND A.EMP_NO = #{emp_no}

SELECT PERSONAL_ANNUITY_AMT AS PERSONAL_ANNUITY_AMT
FROM HANAHR.HR_YT_IN_FNDATA_0 A
WHERE A.YEAR = #{year}
AND A.ADJST_DIV = #{adjst_div}
AND A.EMP_NO = #{emp_no}

SELECT ETC_SUBSCRIPTION_DEPOSIT_AMT AS ETC_SUBSCRIPTION_DEPOSIT_AMT
	,ETC_HOME_MORTGAGE_AMT AS ETC_HOME_MORTGAGE_AMT
