}


function Content_OnUnloadCompleted(obj)
{
    //global.FrameBottom.fcCloseWindow(obj.GetHandle());
}

function fcSetMsgBar(msg) {

    ed_msg.Text = msg;

    this.SetTimer(1000, 9000);
}

function Content_OnTimer(obj,nEventID)
{
	if ( nEventID == 1000 ) {
        ed_msg.Text = "";
	}
}

function Content_OnSize(obj,nCx,nCy,nState)
{
    if ( stFormId.Value == "E02030030" ) {
        div_content.Width                 = this.width;
        div_content.grdRacerArrange.Width = this.width - 20;

    } else if ( stFormId.Value == "E01010120" ) {    
		//trace("nCy : " + nCy + "  formHeight : " + div_content.Grid3.Bottom);		
		if(nCy > 700){
			div_content.setGrid3Size(nCy - 700);
		}else{
			div_content.setGrid3Size(0);
		}
    } else {   		
		div_content.Width = obj.Width ;
		div_content.Height = obj.Height;
    }

    //if (this.height == 588) {
    //    div_msgHLineBottom.visible = false;
    //    div_msgHLineRight.visible = false;
    //} else {
    //    div_msgHLineBottom.visible = true;
    //    div_msgHLineRight.visible = true;
    //}
	//    
	//    ed_msg.top = this.height - ed_msg.height;
	//    div_msgHLine.top = ed_msg.top - 1;
	//    trace("content.height : " + this.height + "\ned_msg.top : " + ed_msg.top + "\ndiv_msgHLine.top : " + div_msgHLine.top);
}
