function fcFormLoadSetting(obj) {
	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//공통코드 조회
	fce_GetComboDs(dsInComCd);

	// 공지사항 조회
	fcSearch(1, sNOTICECLSCD);

	// Q&A 조회
	fcSearch(1, sQNACLSCD);

    // 프로세스 진행현황 조회
    fcGetProcessProg();

    // 마지막 접속 정보 조회
    fcSearchLastLogin();

	SetLastLoginInfo();	// 이전 로그아웃 정보 저장	

	// 매출액현황 조회
	fcGetMoney();

	if(gdsUser.GetColumn(0,"USER_ID")=="99041") {
		var getIP =  ext_GetIPAddress();
		trace(getIP[0]);
		if(getIP[0]!="160.100.52.12") {
			alert("관리자ID가 지정되지 않은 IP에서 접속되었습니다.");
			exit();
		}
	}
//	DeleteShortCut();

	// 화면 초기화
	//fcInit();
}


