/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 공통코드 조회시
	if ( sServiceName == "common-service:searchCode" ) {
//		fce_AddCodeRow(dsOutRacerDgre, "전체", "ALL");
//		fcSearch();
//fnc_DatasetPrint(dsOutMoney);
		if (dsDB.rowcount() > 0) {
			FrameTop.stDBName.text = dsDB.GetColumn(0, "CD_NM");
		}
        //fcGetUbiMoney();
	}

	if( sServiceName == "common_help-service:searchProg") {
	}

    if ( sServiceName == "e01010050-service:searchBoardList" ) {
        fcSetPageCombo();
    }

    if ( sServiceName == "e01010070-service:searchDaysMoney" ) {
        //fcGetUbiMoney();
    }

    if ( sServiceName == "common_login-service:searchLastLoginInfo") {
		if (dsLastLoginInfo.rowcount() > 0) {
			var sLastLoginInfo = "";
			sLastLoginInfo  = " 최근 접속일시 : "+ dsLastLoginInfo.GetColumn(0, "LOGIN_DT");
			sLastLoginInfo += ", "+ dsLastLoginInfo.GetColumn(0, "PC_IP");		
			sLastLoginInfo += ", " + substr(JSESSIONID, Pos(JSESSIONID,".") + 7, 1); 	
			Global.FrameBottom.stLastLoginDt.Text = sLastLoginInfo;
		}
	}
	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}


