function fcSearchLastLogin(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "common_login-service:searchLastLoginInfo";
    var sInDataSet   = "";
    var sOutDataSet  = "dsLastLoginInfo=dsLastLoginInfo";

    fce_AddParam("USER_ID", gdsUser.getColumn(0, "USER_ID"));
	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

function SetLastLoginInfo()
{
	// 마지막 접속 정보를 레지스트리에서 조회하여 최종 종료시간을 저장한다.
	// 최종 종료시간은 1회만 저장이 가능하다(SQL처리:LOGOUT_DT가 NULL 인 경우)
	var sServiceName = "common_login-service:SaveUserTrace";
    var sInDataSet   = "dsUserTrace=dsUserTrace ";
    var sOutDataSet  = "";

    var sLastLogSeq  = GetReg("LastLogSeq");
    var sLastLoginDt = GetReg("LastLoginDt");
    if (sLastLogSeq == "") return;		// 자료가 없으면 종료
    SetReg("LastLogSeq", "");
	SetReg("LastLoginDt", "");

    var arLogSeq  = split(sLastLogSeq, "|");
    var arLoginDt = split(sLastLoginDt, "|");
    var nRow = 0;
    dsUserTrace.ClearData();
    for (var i=1;i<length(arLogSeq);i++) {
		nRow = dsUserTrace.AddRow();
		dsUserTrace.SetColumn(nRow, "LOG_SEQ", arLogSeq[i]);
		dsUserTrace.SetColumn(nRow, "LOGOUT_DT", arLoginDt[i]);
    }
	fce_AddParam("JOB_TYPE", "E");    
	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}

function fcMsgChk() {
	if(left(dsOutNotice.GetColumn(0, "SBJT"),4) == "예행연습") {
		alert("예행연습용 테스트 DB에 연결되어 있습니다.\n 실 운영데이타 입력하지 마세요!!");
	}
}

// 바탕화면 아이콘 지우기
function DeleteShortCut() {
    var desktopFile1 = AliasToRealPath('%desktop%') + '경정운영관리.lnk';
    var desktopFile2 = AliasToRealPath('%desktop%') + 'MBR.lnk';

    if (file0.IsExistFile(desktopFile1)) {
		//alert("바탕화면 바로가기는 삭제됩니다.");
        file0.Delete(desktopFile1);
    }
    if (file0.IsExistFile(desktopFile2)) {
		//alert("바탕화면 바로가기는 삭제됩니다.");
        file0.Delete(desktopFile2);
    }
}
