Window.Script:
// Timer에 의한 주기마다 발생
var aa = 5; 
function form_OnTimer(obj,nEventID) // Timer발생시 Form의 OnTimer Event발동
{
	static_clock.Text = aa;
	aa--;	
	if(aa == 0){
		//reload(); 
		alert(aa + " 경과 후 종료");
		Exit();
		//이쪽에 리로드하는 스크립트를 적용

	}
}

// 1) Timer구동 Button 처리
function Button0_OnClick(obj)
{
	SetTimer(1,1000); // Timer구동
}
