/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {
    if( Arg_clsCd == "001" ) {
        stTxt.Text  = "공지사항 등록";
        obj.Title   = "공지사항";
    }

    if( Arg_clsCd == "002" ) {
        stTxt.Text  = "Q&A 등록";
        obj.Title   = "Q&A";
    }

 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

	//초기 화면 BK Color설정 
	fnc_SetBKColor(this);

    //년도조회
    //spSStndYear.Value = fce_GetSysdate("YYYY");

	//공통코드 조회
	fce_GetComboDs(dsInComCd);

	// 화면 초기화
	//fcInit();

	//fcSearch();
	if ( IsExistVar("Arg_seqNo") ) { // view
        edUserNm.Readonly                    = true;
        edSbjt.Readonly                      = true;
        taBordDesc.Readonly                  = true;
        modifyBtn.visible                    = true;

        replyBtn.visible                     = true;
        //obj.divBtnLst.btnDelete.Visible      = true;
        if( Arg_clsCd == "001" ) stTxt.Text  = "공지사항 조회";
        if( Arg_clsCd == "002" ) stTxt.Text  = "Q&A 조회";
        fnc_SetBKColor(this);

        fcGetQna(Arg_seqNo);
    } else { // write
        // nextseqno 조회

        edUserNm.Readonly                   = true;
        modifyBtn.visible                   = false;
        replyBtn.visible                    = false;
        dsInsertBoard.AddRow();
        dsInsertBoard.setColumn(0, "CLS_CD", Arg_clsCd);
        edUserNm.text = gdsUser.getColumn(0, "USER_NM");
    }

}

