/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {
    stTxt.Text  = "프로세스 진행현황 등록";
    obj.Title   = "프로세스 진행현황";

 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

	//초기 화면 BK Color설정 
	fnc_SetBKColor(this);

    //년도조회
    fce_SetStndYear(dsOutSStndYear, toInteger(fce_GetSysdate("YYYY")));
    cboSStndYear.Index = 0;

	//공통코드 조회
	fce_GetComboDs(dsInComCd);

	// 화면 초기화
	//fcInit();

	//fcSearch();
	if ( IsExistVar("Arg_seqNo") ) { // view
        edSbjt.Readonly                      = true;
        taBordDesc.Readonly                  = true;
        modifyBtn.visible                    = true;

        //obj.divBtnLst.btnDelete.Visible      = true;
        stTxt.Text  = "프로세스 진행현황 조회";
        fnc_SetBKColor(this);

        fcGetBbs(Arg_seqNo);
    } else { // write
        modifyBtn.visible                   = false;
        dsInsertBoard.AddRow();
    }
}

/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 UNLoad時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormUnloadProcess(obj) {
	//자료 변경 여부를 체크하고자 하는  DataSet Object를 넘긴다.
	var sDataset = "dsInsertBoard";
	if ( !fnc_FormUnloadCheck(sDataset , sFormId) ) return false;
}

/*-------------------------------------------------------------------------------------------------+
>>  단축키 처리(Form KeyDown)
+-------------------------------------------------------------------------------------------------*/
function fcHotKey(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {

	//Return Key Press時 Next Component focus
	if ( nChar == 13 ) {
		var oFocusComponent = GetNextComponent(true);
		oFocusComponent.setfocus();
		return;
	}

	//Control Key가 눌려져 있다면 단축키 처리
	//if (bControl) fnc_HotKey(this, nChar);
}

