/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
	return true;
}

function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

    var sServiceName = "e01010080-service:searchRefe";
    var sInDataSet   = "dsOutRefe=dsOutRefe ";
    var sOutDataSet  = "dsOutRefe=dsOutRefe ";

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");    
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {
    return true;
}



function fcSave(obj) {
    if ( !fcSaveCheck(obj) ) return;

    var sServiceName = "e01010080-service:saveRefe";
    var sInDataSet   = "dsOutRefe=dsOutRefe:u ";
    var sOutDataSet  = "dsOutRefe=dsOutRefe ";

    if( dsOutRefe.GetRowCount() > 0 ) {
        for ( i = 0; i < dsOutRefe.GetRowCount(); i++ ) {

            //if(dsOutRefe.GetRowType(i) != "insert" || dsOutRefe.GetRowType(i) != "update") continue;

            if(dsOutRefe.getColumn(i, "MCHK") == 1 ) {
                var mngRtn_arr = fnc_FileWrite(  divBtnLst.HttpFile 
                                            , dsOutRefe.GetColumn(i, "MNG_FILE_URL" ) + "\\" + dsOutRefe.GetColumn(i, "MNG_FILE_NAME" )
                                            , dsOutRefe.GetColumn(i, "MNG_FILE_NAME" )
                                            , "REFEREE");

                if ( mngRtn_arr[0] != "SUCC" ) {
                    alert("파일 업로드 에러발생");
                    return false;
                } else {
                    var fileSize = divBtnLst.HttpFile.GetFileSizeLocal(dsOutRefe.GetColumn(i, "MNG_FILE_URL" ) + "\\" + dsOutRefe.GetColumn(i, "MNG_FILE_NAME" ));
                    dsOutRefe.setColumn(i, "MNG_FILE_URL", mngRtn_arr[1]);
                    dsOutRefe.setColumn(i, "MNG_FILE_SIZE", fileSize);
                }
            }

            if(dsOutRefe.getColumn(i, "RCHK") == 1 ) {
                var refeRtn_arr = fnc_FileWrite(  divBtnLst.HttpFile 
                                            , dsOutRefe.GetColumn(i, "REFE_HEAD_FILE_URL" ) + "\\" + dsOutRefe.GetColumn(i, "REFE_HEAD_FILE_NAME" ) 
                                            , dsOutRefe.GetColumn(i, "REFE_HEAD_FILE_NAME" )
                                            , "REFEREE");

                if ( refeRtn_arr[0] != "SUCC" ) {
                    alert("파일 업로드 에러발생");
                    return false;
                } else {
                    var fileSize = divBtnLst.HttpFile.GetFileSizeLocal(dsOutRefe.GetColumn(i, "REFE_HEAD_FILE_URL" ) + "\\" + dsOutRefe.GetColumn(i, "REFE_HEAD_FILE_NAME" ));
                    dsOutRefe.setColumn(i, "REFE_HEAD_FILE_URL", refeRtn_arr[1]);
                    dsOutRefe.setColumn(i, "REFE_HEAD_FILE_SIZE", fileSize);
                }
            }

            if(dsOutRefe.getColumn(i, "ICHK") == 1 ) {
                var inspRtn_arr = fnc_FileWrite(  divBtnLst.HttpFile 
                                            , dsOutRefe.GetColumn(i, "INSP_CMAN_FILE_URL" ) + "\\" + dsOutRefe.GetColumn(i, "INSP_CMAN_FILE_NAME" )
                                            , dsOutRefe.GetColumn(i, "INSP_CMAN_FILE_NAME" ) 
                                            , "REFEREE");

                if ( inspRtn_arr[0] != "SUCC" ) {
                    alert("파일 업로드 에러발생");
                    return false;
                } else {
                    var fileSize = divBtnLst.HttpFile.GetFileSizeLocal(dsOutRefe.GetColumn(i, "INSP_CMAN_FILE_URL" ) + "\\" + dsOutRefe.GetColumn(i, "INSP_CMAN_FILE_NAME" ));
                    dsOutRefe.setColumn(i, "INSP_CMAN_FILE_URL", inspRtn_arr[1]);
                    dsOutRefe.setColumn(i, "INSP_CMAN_FILE_SIZE", fileSize);
                }
            } // end if
        } // end for   
    } // end if

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");   
}


