/*-------------------------------------------------------------------------------------------------+
>>  Help Popup Open
+-------------------------------------------------------------------------------------------------*/
function fcHelpDialoge(obj,nX,nY) {
    var arrReturn    = array(2);
    arrReturn = Dialog("form::E01010081.xml", "", 334, 443, "");

    if(arrReturn[0] != "") {
        switch(obj.id) {
            case "btnMng" :
                    edMng.text = arrReturn[1];
                    dsOutRefe.setColumn(dsOutRefe.currow, "MNG", arrReturn[0]);
                    break;
            case "btnRefe" :
                    edRefeHead.text = arrReturn[1];
                    dsOutRefe.setColumn(dsOutRefe.currow, "REFE_HEAD", arrReturn[0]);
                    break;
            case "btnInsp" :
                    edInspCman.text = arrReturn[1];
                    dsOutRefe.setColumn(dsOutRefe.currow, "INSP_CMAN", arrReturn[0]);
                    break;
        }
    }
}

