/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

    // 심판담당자 조회
    if ( sServiceName == "e01010080-service:searchRefe" ) {
        dsOutRefe.addColumn("CHK");
        dsOutRefe.addColumn("MCHK");
        dsOutRefe.addColumn("RCHK");
        dsOutRefe.addColumn("ICHK");
        fcSetImageID(0);
    }    

    if ( sServiceName = "e01010080-service:saveRefe" ) {
        fcSearch();
    }
}


/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {

	//DataSet의 Header 정보가 있을 때  Add 처리
	switch ( obj.GetForm().id ) {            
		case "Div2" :
            var sArg = "";

			dsOutRefe.AddRow();         //Record Add

			edMngFileName.Readonly      = false;
			edRefeHeadFileName.Readonly = false;
			edInspCmanFileName.Readonly = false;

			img1.ImageID = "";
			img2.ImageID = "";
			img3.ImageID = "";

			fnc_SetBKColor(this);

			break;			
    } // end switch.case
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {

	//DataSet의 Header 정보가 있을 때  Add 처리 	
	switch ( obj.GetForm().id ) {
		case "Div2" :
			if ( !fnc_DeleteCheck(dsOutRefe, "chk", "1") ) return;
			dsOutRefe_OnRowPosChanged();            
			break;            
    } // end switch.case
}


function imgMng_OnClick(obj,nX,nY)
{   
    sResponse = fce_GetSelectFile();
    switch(obj.id) {
        case "imgMng" :
            dsOutRefe.setColumn(dsOutRefe.currow, "MCHK", 1);
            dsOutRefe.setColumn(dsOutRefe.currow, "MNG_FILE_URL" , sResponse[0]);
            dsOutRefe.setColumn(dsOutRefe.currow, "MNG_FILE_NAME", sResponse[1]);
            //var fileSize = divBtnLst.HttpFile.GetFileSizeLocal(dsOutRefe.GetColumn(dsOutRefe.currow, "MNG_FILE_URL" ));
            //dsOutRefe.setColumn(dsOutRefe.currow, "MNG_FILE_SIZE", fileSize);
            edMngFileName.Text = sResponse[1];
            break;
        case "imgRefe" :
            dsOutRefe.setColumn(dsOutRefe.currow, "RCHK", 1);
            dsOutRefe.setColumn(dsOutRefe.currow, "REFE_HEAD_FILE_URL" , sResponse[0]);
            dsOutRefe.setColumn(dsOutRefe.currow, "REFE_HEAD_FILE_NAME", sResponse[1]);
            //var fileSize = divBtnLst.HttpFile.GetFileSizeLocal(dsOutRefe.GetColumn(dsOutRefe.currow, "REFE_FILE_URL" ));
            //dsOutRefe.setColumn(dsOutRefe.currow, "REFE_FILE_SIZE", fileSize);
            edRefeHeadFileName.Text = sResponse[1];
            break;        
        case "imgInspCman" :
            dsOutRefe.setColumn(dsOutRefe.currow, "ICHK", 1);
            dsOutRefe.setColumn(dsOutRefe.currow, "INSP_CMAN_FILE_URL" , sResponse[0]);
            dsOutRefe.setColumn(dsOutRefe.currow, "INSP_CMAN_FILE_NAME", sResponse[1]);
            //var fileSize = divBtnLst.HttpFile.GetFileSizeLocal(dsOutRefe.GetColumn(dsOutRefe.currow, "INSP_CMAN_FILE_URL" ));
            //dsOutRefe.setColumn(dsOutRefe.currow, "INSP_CMAN_FILE_SIZE", fileSize);
            edInspCmanFileName.Text = sResponse[1];
            break;        
    } // end switch...case
}

function fcSetImageID(nRow) {
    var url1 = split(dsOutRefe.getColumn(nRow, "MNG_FILE_URL"), "/");
    var url2 = split(dsOutRefe.getColumn(nRow, "REFE_HEAD_FILE_URL"), "/");
    var url3 = split(dsOutRefe.getColumn(nRow, "INSP_CMAN_FILE_URL"), "/");

    img1.imageID = GBL_MNGLINK_URL + url1[url1.length-1];
    img2.imageID = GBL_MNGLINK_URL + url2[url2.length-1];
    img3.imageID = GBL_MNGLINK_URL + url3[url3.length-1];
}

function dsOutRefe_OnRowPosChanged(obj,nOldRow,nRow)
{
    fcSetImageID(nRow);
    if ( dsOutRefe.GetRowCount() == 0 ) return;

    if ( dsOutRefe.GetRowCount() <= dsOutRefe.currow ) {
        dsOutRefe.FireEvent = false;
        dsOutRefe.row = dsOutRefe.GetRowCount() - 1;
        dsOutRefe.SelectRow(dsOutRefe.GetRowCount() - 1);
        dsOutRefe.FireEvent = true;
    }
}
