}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck() {
   if (!fnc_IsMandatory(cboStndYear,	"년도"))	return false;
   if (!fnc_IsMandatory(cboMbrCd,		"경정장"))	return false;
   if (!fnc_IsMandatory(cboTMS,			"회차"))	return false;
   if (!fnc_IsMandatory(cboDayOrd,		"일차"))	return false;
   if (!fnc_IsMandatory(cboRACE_NO,		"경주"))	return false;

}

//조회 
function fcSearch(obj) {
	if(fcSearchCheck() == false) return;

    var sServiceName = "e01010120-service:searchRacer";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutRacer=dsOutRacer";

    fce_AddParam("STND_YEAR", cboStndYear.Value);
    fce_AddParam("MBR_CD", cboMbrCd.Value);
    fce_AddParam("TMS", cboTMS.Value);
    fce_AddParam("DAY_ORD", cboDayOrd.Value);
    fce_AddParam("RACE_NO", cboRACE_NO.Value);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

//경주확정정보 조회
function fcSearchCloseRaceInfo()
{
	var sServiceName = "e01010150-service:searchCloseRaceInfo";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutCloseRaceInfo=dsOutCloseRaceInfo";

    fce_AddParam("STND_YEAR", cboStndYear.Value);
    fce_AddParam("MBR_CD", "003");	//MBR_CD -> MEET_CD :'003'
    fce_AddParam("TMS", cboTMS.Value);
    fce_AddParam("DAY_ORD", cboDayOrd.Value);
    fce_AddParam("RACE_NO", cboRACE_NO.Value);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}
/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutCloseRaceInfo))
	{
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}

	// 필수항목체크
	oDataSet          = dsOutCloseRaceInfo;
	sMandatoryColID   = "POST_DT|CURR_DT|REST_MM|RACE_END_YN";
	sMandatoryColName = "발매마감시간|현재시간|잔여시간|경주마감여부";
	oGrid             = grdPageList;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}
	return true;
}

function fcSave(obj) {
	if ( !fcSaveCheck() ) return;
	var sServiceName = "e01010150-service:saveCloseRaceInfo";
    var sInDataSet   = "dsOutCloseRaceInfo=dsOutCloseRaceInfo:u";
    var sOutDataSet  = "dsOutCloseRaceInfo=dsOutCloseRaceInfo";

	fce_AddParam("STND_YEAR", cboStndYear.Value);
    fce_AddParam("MBR_CD", "003");	//MBR_CD -> MEET_CD :'003'
    fce_AddParam("TMS", cboTMS.Value);
    fce_AddParam("DAY_ORD", cboDayOrd.Value);
    fce_AddParam("RACE_NO", cboRACE_NO.Value);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

