/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
	if (dsOutRacer.GetRowCount() <= 0) return;
	if (dsOutCloseRaceInfo.ColCount() > 0) {
		var nRow = dsOutCloseRaceInfo.AddRow();        	  //Record Add
		dsOutCloseRaceInfo.SetColumn(nRow, "MEET_CD", 		"003");
		dsOutCloseRaceInfo.SetColumn(nRow, "STND_YEAR", 	dsOutRacer.GetColumn(0, "STND_YEAR"));
		dsOutCloseRaceInfo.SetColumn(nRow, "TMS", 			dsOutRacer.GetColumn(0, "TMS"));
		dsOutCloseRaceInfo.SetColumn(nRow, "DAY_ORD", 		dsOutRacer.GetColumn(0, "DAY_ORD"));
		dsOutCloseRaceInfo.SetColumn(nRow, "RACE_NO", 		dsOutRacer.GetColumn(0, "RACE_NO"));

	} else {
		fnc_Message(SNIS_COM_0002, "N");      //DataSet의 Header가 없어서 오류
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	//DataSet의 Header 정보가 있을 때  Add 처리 	
	if ( !fnc_DeleteCheck(dsOutCloseRaceInfo, "chk", "1") ) return;
	grdPageList.SetFocus(); 
}

