/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 조회 후
	if ( sServiceName == "e01010150-service:searchCloseRaceInfo" ) {
	    if(dsOutCloseRaceInfo.GetRowCount() > 0){
			fnc_SetStatus(SNIS_BOA_0003, dsOutCloseRaceInfo.GetRowCount());

	    }
	}

	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

