//row 변경시 
function dsOutCloseRaceInfo_OnRowPosChanged(obj,nOldRow,nRow)
{
	LATEST_ROW_POS = nOldRow;
}


//DataSet 정상  로드시  (저장후 조회시 저장시점으 Rowposition유지)
function dsOutCloseRaceInfo_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	fnc_ProgressClose(); //팝업창이 Close안되었으면 Close시킨다.
	if(LATEST_ROW_POS > 0) dsOutCloseRaceInfo.Row = LATEST_ROW_POS;
}

