//경주번호 DataSet Load 완료 시 
function dsOutSRace_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	cboRACE_NO.Index = 0;
}

function dsOutRacer_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	var nRow = dsOutRacer.GetRowCount();
	var sRACE_REG_NO;
	var sRacer;

	if (nRow <= 0) 
	{
		stRacer.Text = "";
		dsOutCloseRaceInfo.ClearData();
		return;
	}

	for (var i = 0; i < nRow; i++)
	{
		sRACE_REG_NO = dsOutRacer.GetColumn(i, "RACE_REG_NO");
		sRank		 = dsOutRacer.GetColumn(i, "RANK");
		sRacer += sRACE_REG_NO + "-" + dsOutRacer.GetColumn(i, "NM_KOR");

		if (sRank != "")
			sRacer += "(" + sRank + "착)";

		if (i < nRow - 1)
			sRacer += ", ";
	}
	stRacer.Text = "출전선수 : " + sRacer;
	fcSearchCloseRaceInfo();
}
