/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_Result(ErrorCode, ErrorMSG) ) return;

	// 공통코드 조회시
	if ( sServiceName == "common-service:searchCode" ) {
        cboSMbr.Index = 0;
        fcSetDt();
        fce_GetRaceTms(cboSStndYear.Value,cboSMbr.Value,"","dsOutSRaceTms");
		return;
	}

	// 회차 리스트 조회시
	if ( sServiceName == "common_race-service:searchRaceTms" ) {
        cboSRaceTms.Index = 0;
        if ( cboSStndYear.Value == fce_GetCurRaceInfo("STND_YEAR") ) {
            var nIndexRow = fnc_GetExistValue(dsOutSRaceTms, "TMS", fce_GetCurRaceInfo("TMS"));
            if ( nIndexRow > -1 ) cboSRaceTms.Index = nIndexRow;
        }

        fce_GetRaceDayOrd(cboSStndYear.Value, cboSMbr.Value, cboSRaceTms.Value, "dsOutSDayOrd");
		return;
	}

	// 일차 리스트 조회시
	if ( sServiceName == "common_race-service:searchRaceDayOrd" ) {
        cboSRaceDayOrd.Index = 0;
        if ( cboSStndYear.Value == fce_GetCurRaceInfo("STND_YEAR") 
          && cboSRaceTms.Value  == fce_GetCurRaceInfo("TMS") ) {
            var nIndexRow = fnc_GetExistValue(dsOutSDayOrd, "DAY_ORD", fce_GetCurRaceInfo("DAY_ORD"));
            if ( nIndexRow > -1 ) cboSRaceDayOrd.Index = nIndexRow;
        }

		return;
	}

	if ( sServiceName == "e01010210-service:searchRaceRslt_1" ) {
/*
        var varArray = Array(10);
            varArray[0] = 1;
        var asColumns = Array(6);

        asColumns[0] = "FIXMONEY_W_CNT";
        asColumns[1] = "FIXMONEY_P_CNT";
        asColumns[2] = "FIXMONEY_P2_CNT";
        asColumns[3] = "FIXMONEY_E_CNT";
        asColumns[4] = "FIXMONEY_Q_CNT";
        asColumns[5] = "FIXMONEY_T_CNT";

        for ( var i = 0; i < length(asColumns); i++ ) {
            dsOutRaceRslt_2.AddColumn(asColumns[i]);
        }

        for ( var i = 0; i < dsOutRaceRslt_2.GetRowCount(); i++ ) {
            for ( var j = 0; j < Length(asColumns); j++ ) {
                if ( !fnc_IsNull(dsOutRaceRslt_2.GetColumn(i, substr(asColumns[j], 0, length(asColumns[j]) - 4))) ) {
                    dsOutRaceRslt_2.SetColumn(i, asColumns[j], 1);
                }
            }
        }
        */
        return;
	}

	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

