function cboSStndYear_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    fcInit();
    fce_GetRaceTms(cboSStndYear.value, cboSMbr.Value, "", "dsOutSRaceTms");	
}

function cboSRaceTms_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    fcInit();
    fce_GetRaceDayOrd(cboSStndYear.Value, cboSMbr.Value, cboSRaceTms.Value, "dsOutSDayOrd");	
}

function cboSRaceDayOrd_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fcInit();
}

function fcSetDt()
{
    for ( var i = 0; i < dsOutDt.GetRowCount(); i++ ) {
        tb.tb1.grdRaceRslt_5.SetCellProp('head', i + 1, 'text' , dsOutDt.GetColumn(i, "BRNCH_NM"));
        tb.tb1.grdRaceRslt_5.SetCellProp('body', i + 1, 'colId', "DIV_NO_" + dsOutDt.GetColumn(i, "DIV_NO"));
//        tb.tb1.grdRaceRslt_5.SetCellProp('summery', i + 1, 'Expr', "Sum('DIV_NO_" + dsOutDt.GetColumn(i, "DIV_NO")) + "')";

        tb.tb1.grdRaceRslt_6.SetCellProp('head', i + 1, 'text' , dsOutDt.GetColumn(i, "BRNCH_NM"));
        tb.tb1.grdRaceRslt_6.SetCellProp('body', i + 1, 'colId', "DIV_NO_" + dsOutDt.GetColumn(i, "DIV_NO"));
    }
}
