/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
    dsOutProcess.ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
    if ( rdoOption.Value == "002" ) {
        if ( !fnc_IsMandatory(calStrDt    , "일자"  ) ) return false;
    } else {
        if ( !fnc_IsMandatory(cboSStndYear, "년도"  ) ) return false;
        if ( !fnc_IsMandatory(cboSMbr     , "경정장") ) return false;
        if ( !fnc_IsMandatory(cboSRaceTms , "회차"  ) ) return false;
    }

    fcInit(obj);
 	return true;
}

function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "e01010220-service:searchProcess";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutProcess=dsOutProcess ";

	fce_AddParam("STND_YEAR", cboSStndYear.Value);
	fce_AddParam("MBR_CD"   , cboSMbr.Value     );
	fce_AddParam("TMS"      , cboSRaceTms.Value );
	fce_AddParam("DAY_ORD"  , cboSDayOrd.Value  );
	fce_AddParam("DT"       , calStrDt.Value    );
	fce_AddParam("MONTH"    , cboMonth.Value    );
	fce_AddParam("OPTION"   , rdoOption.Value   );

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {
 	return true;
}

function fcSave(obj) {
    if ( !fcSaveCheck(obj) ) return;

	var sServiceName = "e01010220-service:saveProcess";
    var sInDataSet   = "dsOutProcess=dsOutProcess:u";
    var sOutDataSet  = "dsOutProcess=dsOutProcess ";

	fce_AddParam("STND_YEAR", cboSStndYear.Value);
	fce_AddParam("MBR_CD"   , cboSMbr.Value     );
	fce_AddParam("TMS"      , cboSRaceTms.Value );
	fce_AddParam("DAY_ORD"  , cboSDayOrd.Value  );
	fce_AddParam("DT"       , calStrDt.Value    );
	fce_AddParam("MONTH"    , cboMonth.Value    );
	fce_AddParam("OPTION"   , rdoOption.Value   );

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

