/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit() {
    dsUserListValue.ClearData();

}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 체크 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck() {
    //조회조건 체크및 trim처리

	fcInit();    //기존 데이터셋 초기화

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch() {
    if ( !fcSearchCheck() ) return;

	var sServiceName = "e01010240-service:searchMenuGrpValue";
    var sInDataSet   = "";
    var sOutDataSet  = "dsMenuGrpValue=dsMenuGrpValue ";
        sOutDataSet += "dsMenuGrpList=dsMenuGrpList ";  

	var sMnGrpCd = "";
    sMnGrpCd=cboMnGrpCd.Value;

	fce_AddParam("MN_GRP", sMnGrpCd);
	//fce_AddParam("USER_ID", sUserID);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

/*-------------------------------------------------------------------------------------------------+
>>  메뉴그룹에 메뉴 클릭했을 時 처리
+-------------------------------------------------------------------------------------------------*/

var lastSelRow = -1;
/*-------------------------------------------------------------------------------------------------+
>>  사용자클릭시 사용자메뉴권한 리스트 조회
+-------------------------------------------------------------------------------------------------*/
function fcSearchAuthUserList(nRow) {
	if ( !fcSearchCheck() ) return;

	if(nRow != null && nRow > -1){
		dsMenuGrpValue.row = nRow;
	}

    if(LENGTH(dsMenuGrpValue.GetColumn(dsMenuGrpValue.row,"UP_MN_NM")) >0){
		var sServiceName = "e01010240-service:searchUserList";
		var sInDataSet   = "";
		var sOutDataSet  = "dsUserListValue=dsUserListValue ";

		fce_AddParam("MN_ID", dsMenuGrpValue.GetColumn(dsMenuGrpValue.row,"MN_ID"));
		fce_AddParam("DEPT_CD", cboTeamNm.value);

		fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 저장가능유무처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {  
	if ( !fnc_DatasetChangeCheck(dsUserListValue) ) {
		// 저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {

    if ( !fcSaveCheck() ) return;

    var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";    

    sServiceName = "e01010240-service:saveMenuAuth";
    sInDataSet   = "dsMenuGrpValue=dsMenuGrpValue:u ";
    sInDataSet  += "dsUserListValue=dsUserListValue:u ";


	dsMenuGrpValue.SetColumn(dsMenuGrpValue.row,"CHK","1");

    sOutDataSet  = "";

    for ( var i = 0; i < dsUserListValue.rowcount; i++ ) {        
        if(    dsUserListValue.getColumn(i, "SRCH_YN") == 1
            || dsUserListValue.getColumn(i, "INPT_YN") == 1) {
            if(dsUserListValue.getColumn(i, "SRCH_YN") != 1) dsUserListValue.setColumn(i, "SRCH_YN", 0);
            if(dsUserListValue.getColumn(i, "INPT_YN") != 1) dsUserListValue.setColumn(i, "INPT_YN", 0);
        }
    }

    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}


/*-------------------------------------------------------------------------------------------------+
>>  엑셀 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcToExcel(obj) {
    var sExcelList = "grdUserList:사용자리스트"; //데이터셋 정의
    fce_OpenExcelListExport(sExcelList);
}


