/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	if(sServiceName == "e01010240-service:searchMenuGrpValue"){
		if(LENGTH(dsMenuGrpValue.GetColumn(dsMenuGrpValue.row,"MN_ID")) >0){
			fcSearchAuthUserList();
		}	
	}

	if(sServiceName == "e01010240-service:saveMenuAuth"){
		alert("저장되었습니다.");
		fcSearch();
	}
}

