}

function dsOutMNGrp_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{    
	dsOutMNGrp.InsertRow(0);
	dsOutMNGrp.SetColumn(0, "MN_NM", "전체");
	cboMnGrpCd.index = 0;
}

function dsAuthListValue_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	var chkYN = dsUserListValue.GetColumn(nRow, strColumnID);

	if(strColumnID == "SRCH_YN" && chkYN == 0) {
        dsUserListValue.SetColumn(nRow, "INPT_YN", 0);
	}
	if(strColumnID != "SRCH_YN" && chkYN == 1) {
        dsUserListValue.SetColumn(nRow, "SRCH_YN", 1);
	}
}

function chk_OnClick(obj,strValue)
{
    for ( var i = 0; i < dsUserListValue.rowcount; i++ ) {
		if(obj.id == "chkSrch" && obj.value == 0) {              //조회 언체크하면 저장,승인 자동 언체크
			chkSave.Value = 0;
			dsUserListValue.setColumn(i, "SRCH_YN", 0);
			dsUserListValue.setColumn(i, "INPT_YN", 0);
		} else if(obj.id != "chkSrch" && obj.value == 1) {       //저장,승인 체크하면 조회부분 자동체크되도록
			chkSrch.Value = 1;
			dsUserListValue.setColumn(i, "SRCH_YN", chkSrch.Value);

			if(obj.id == "chkSave"){
				dsUserListValue.setColumn(i, "INPT_YN", chkSave.Value);
			}

		} else {    
			if(obj.id == "chkSave"){
				dsUserListValue.setColumn(i, "INPT_YN", chkSave.Value);
			}else if(obj.id == "chkSrch"){
				dsUserListValue.setColumn(i, "SRCH_YN", chkSrch.Value);
			}
		}
	}
}


function dsOutTeamNm_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	dsOutTeamNm.InsertRow(0);
	dsOutTeamNm.SetColumn(0, "TEAM_NM", "전체");
	cboTeamNm.index = 0;
}

function fcCboOnchanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fcSearchAuthUserList();
}

function dsUserListValue_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	var chkYN = dsUserListValue.GetColumn(nRow, strColumnID);

	if(strColumnID == "SRCH_YN" && chkYN == 0) {
        dsUserListValue.SetColumn(nRow, "INPT_YN", 0);
	}
	if(strColumnID != "SRCH_YN" && chkYN == 1) {
        dsUserListValue.SetColumn(nRow, "SRCH_YN", 1);
	}
}

function dsMenuGrpValue_CanRowPosChange(obj,nOldRow,nRow)
{
	fcSearchAuthUserList(nRow);
}
