function fcInit(obj) {	
    dsOutRankScr.ClearData();
    dsOutGppScr.ClearData();
    dsOutAcdntScr.ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  Á¶È¸ ¹öÆ° Å¬¸¯ ãÁ Ã³¸®
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck() {
    dsOutRankScr.ClearData();
    dsOutGppScr.ClearData();
    dsOutAcdntScr.ClearData();

	return true;
}

function fcSearch(obj) {
    if ( !fcSearchCheck() ) return;

	var sServiceName = "e02010010-service:searchScore";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutRankScr=dsOutRankScr dsOutGppScr=dsOutGppScr dsOutAcdntScr=dsOutAcdntScr";

	fce_AddParam(Trim("STND_YEAR"), cboSStndYear.Value);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}


/*-------------------------------------------------------------------------------------------------+
>>  ÀúÀå ¹öÆ° Å¬¸¯ ãÁ Ã³¸®
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	//º¯°æµÈ ÀÚ·á°¡ ÀÖ´ÂÁö Ã¼Å©
	if ( !fnc_DatasetChangeCheck(dsOutRankScr ) 
	  && !fnc_DatasetChangeCheck(dsOutGppScr ) 
	  && !fnc_DatasetChangeCheck(dsOutAcdntScr) )
	{
		//ÀúÀå ÇÒ ÀÚ·á°¡ ¾øÀ½
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}

	// ÇÊ¼öÇ×¸ñÃ¼Å©
	oDataSet          = dsOutRankScr;
	sMandatoryColID   = "RACE_DGRE_CD|RANK_1|RANK_2|RANK_3|RANK_4|RANK_5|RANK_6";
	sMandatoryColName = "°æÁÖµî±Þ|1ÂøÁ¡¼ö|2ÂøÁ¡¼ö|3ÂøÁ¡¼ö|4ÂøÁ¡¼ö|5ÂøÁ¡¼ö|6ÂøÁ¡¼ö";
	oGrid             = grdRankScr;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

	// ÇÊ¼öÇ×¸ñÃ¼Å©
	oDataSet          = dsOutGppScr;
	sMandatoryColID   = "DAY_ORD|RACE_DGRE_CD|RANK_1|RANK_2|RANK_3|RANK_4|RANK_5|RANK_6|RANK_0";
	sMandatoryColName = "ÀÏÀÚ|°æÁÖµî±Þ|1ÂøÁ¡¼ö|2ÂøÁ¡¼ö|3ÂøÁ¡¼ö|4ÂøÁ¡¼ö|5ÂøÁ¡¼ö|6ÂøÁ¡¼ö|¸éÃ¥½Ç°ÝÁ¡¼ö";
	oGrid             = grdGppScr;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

	// ÇÊ¼öÇ×¸ñÃ¼Å©
	oDataSet          = dsOutAcdntScr;
	sMandatoryColID   = "VOIL_CD|ACDNT_SCR";
	sMandatoryColName = "À§¹ÝÄÚµå|»ç°íÁ¡";
	oGrid             = grdAcdntScr;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

	return true;
}

function fcSave(obj) {

    if ( !fcSaveCheck() ) return;

	var sServiceName = "e02010010-service:saveScore";
    var sInDataSet   = "dsOutRankScr=dsOutRankScr:u dsOutGppScr=dsOutGppScr:u dsOutAcdntScr=dsOutAcdntScr:u";
    var sOutDataSet  = "dsOutRankScr=dsOutRankScr dsOutGppScr=dsOutGppScr dsOutAcdntScr=dsOutAcdntScr";

	fce_AddParam(Trim("STND_YEAR"), cboSStndYear.Value);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

