function fcAddRow(obj) {

	//DataSetÀÇ Header Á¤º¸°¡ ÀÖÀ» ¶§  Add Ã³¸® 
	switch ( obj.GetForm().id ) {
		case "divBtnRowRankScr":
            dsOutRankScr.AddRow();                     //Record Add
            grdRankScr.SetFocus();         //ÀÔ·Â Ã¹ Ç×¸ñÀ¸·Î Focus
            break;
		case "divBtnRowGppScr":
            dsOutGppScr.AddRow();                    //Record Add
            grdGppScr.SetFocus();          //ÀÔ·Â Ã¹ Ç×¸ñÀ¸·Î Focus
            break;
		case "divBtnRowAcdntScr":
            dsOutAcdntScr.AddRow();                    //Record Add
            grdAcdntScr.SetFocus();        //ÀÔ·Â Ã¹ Ç×¸ñÀ¸·Î Focus
            break;
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  Çà»èÁ¦ ¹öÆ° Å¬¸¯ ãÁ Ã³¸®
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	switch ( obj.GetForm().id ) {
		case "divBtnRowRankScr" :
            //»èÁ¦ ÇÒ ÀÚ·á°¡ ÀÖ´ÂÁö ¿©ºÎ¸¦ Ã¼Å©
			if ( !fnc_DeleteCheck(dsOutRankScr, "chk", "1") ) return;
            grdRankScr.SetFocus();                          //Record »èÁ¦ ÈÄ Grid·Î Focus
            break;
		case "divBtnRowGppScr" :
            //»èÁ¦ ÇÒ ÀÚ·á°¡ ÀÖ´ÂÁö ¿©ºÎ¸¦ Ã¼Å©
			if ( !fnc_DeleteCheck(dsOutGppScr, "chk", "1") ) return;
            grdGppScr.SetFocus();                          //Record »èÁ¦ ÈÄ Grid·Î Focus
            break;
		case "divBtnRowAcdntScr" :
            //»èÁ¦ ÇÒ ÀÚ·á°¡ ÀÖ´ÂÁö ¿©ºÎ¸¦ Ã¼Å©
			if ( !fnc_DeleteCheck(dsOutAcdntScr, "chk", "1") ) return;
            grdAcdntScr.SetFocus();                      //Record »èÁ¦ ÈÄ Grid·Î Focus
			break;
	}
}

