/*-------------------------------------------------------------------------------------------------+
>>  Transaction ÈÄ Ã³¸® ÇØ¾ß ÇÒ ³»¿ë!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_Result(ErrorCode, ErrorMSG) ) return;

	// °øÅëÄÚµå Á¶È¸½Ã
	if ( sServiceName == "common-service:searchCode" ) {
        fcSearch();
        return;
	}

	// Á¶È¸ ÈÄ
	if ( sServiceName == "e02010010-service:searchScore" ) {
        dsOutRankScr.AddColumn("chk");
        dsOutGppScr.AddColumn("chk");
        dsOutAcdntScr.AddColumn("chk");
        fnc_SetStatus(SNIS_BOA_0003, dsOutRankScr.GetRowCount() + dsOutGppScr.GetRowCount() + dsOutAcdntScr.GetRowCount());
        return;
	}

	// ÀúÀå ÈÄ
	if ( sServiceName == "e02010010-service:saveScore" ) {
        dsOutRankScr.AddColumn("chk");
        dsOutGppScr.AddColumn("chk");
        dsOutAcdntScr.AddColumn("chk");
        return;
	}

	//¿À·ù°¡ ¾øÀÌ Á¤»ó Ã³¸®µÇ¾ú´Ù¸é...
	fcPostProcess(sServiceName);
}

