/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {

	//DataSet의 Header 정보가 있을 때  Add 처리 
	switch ( obj.GetForm().id ) {
		case "divBtnRowRankScr":
            dsOutRankScr.AddRow();                     //Record Add
            grdRankScr.SetFocus();         //입력 첫 항목으로 Focus
            break;
		case "divBtnRowGppScr":
            dsOutGppScr.AddRow();                    //Record Add
            grdGppScr.SetFocus();          //입력 첫 항목으로 Focus
            break;
		case "divBtnRowAcdntScr":
            dsOutAcdntScr.AddRow();                    //Record Add
            grdAcdntScr.SetFocus();        //입력 첫 항목으로 Focus
            break;
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	switch ( obj.GetForm().id ) {
		case "divBtnRowRankScr" :
            //삭제 할 자료가 있는지 여부를 체크
			if ( !fnc_DeleteCheck(dsOutRankScr, "chk", "1") ) return;
            grdRankScr.SetFocus();                          //Record 삭제 후 Grid로 Focus
            break;
		case "divBtnRowGppScr" :
            //삭제 할 자료가 있는지 여부를 체크
			if ( !fnc_DeleteCheck(dsOutGppScr, "chk", "1") ) return;
            grdGppScr.SetFocus();                          //Record 삭제 후 Grid로 Focus
            break;
		case "divBtnRowAcdntScr" :
            //삭제 할 자료가 있는지 여부를 체크
			if ( !fnc_DeleteCheck(dsOutAcdntScr, "chk", "1") ) return;
            grdAcdntScr.SetFocus();                      //Record 삭제 후 Grid로 Focus
			break;
	}
}

