/*-------------------------------------------------------------------------------------------------+
>>  경주등급 Column의 값이 변경된 경우
+-------------------------------------------------------------------------------------------------*/
function dsOutRankScr_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if ( strColumnID == "RACE_DGRE_CD" ) {
	    if ( fnc_GetExistValue(obj, strColumnID, varNewValue) != nRow ) {
			fnc_Message(L_COM_ALT_0016, "N", "착순점이");
			obj.row = nRow;
			obj.SetColumn(nRow, strColumnID, varOldValue);
	    }
	    if ( fnc_GetExistValue(obj, strColumnID, varNewValue, nRow + 1) > 0 ) {
			fnc_Message(L_COM_ALT_0016, "N", "착순점이");
			obj.row = nRow;
			obj.SetColumn(nRow, strColumnID, varOldValue);
	    }
	}
}


/*-------------------------------------------------------------------------------------------------+
>>  위반코드 Column의 값이 변경된 경우
+-------------------------------------------------------------------------------------------------*/
function dsOutAcdntScr_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if ( strColumnID == "VOIL_CD" ) {
	    if ( fnc_GetExistValue(obj, strColumnID, varNewValue) != nRow ) {
			fnc_Message(L_COM_ALT_0016, "N", "사고점이");
			obj.row = nRow;
			obj.SetColumn(nRow, strColumnID, varOldValue);
	    }
	    if ( fnc_GetExistValue(obj, strColumnID, varNewValue, nRow + 1) > 0 ) {
			fnc_Message(L_COM_ALT_0016, "N", "사고점이");
			obj.row = nRow;
			obj.SetColumn(nRow, strColumnID, varOldValue);
	    }
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  년도 변경시
+-------------------------------------------------------------------------------------------------*/
function cboSStndYear_OnChanged(obj,strText)
{
	fcSearch();
}
