***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_Result(ErrorCode, ErrorMSG) ) return;

	// 공통코드 조회 완료 후
	if ( sServiceName == "common-service:searchCode" ) {                        
        cboEndPerio.Index = dsOutRacerPerioNo.GetRowCount() - 1;
        fcRacerCondProcess();
		fcSearchRecdStndTerm();
		cboSQurt.Index = 0;
		dsOutRacerGrd.Copy(dsOutSRacerGrd);
        fce_AddCodeRow(dsOutSRacerGrd, "전체", "", cboSRacerGrd, false);

        return;
	}

	// 조회 후
	if ( sServiceName == "e02010040-service:searchRecdStndTerm" ) {
        cboSQurt_OnChanged();
        return;
    }

	// 조회 후
	if ( sServiceName == "e02020020-service:searchRacerGrdRaceCnt" ) {
        fcSetRaceCnt();
        fcSetRacerCnt();
        return;
    }

	// 조회 후
	if ( sServiceName == "e02020020-service:searchRacerDegree" ) {
        return;

    }
    if ( sServiceName == "e02020030-service:searchRacerRankDetail" ) {
        fcSetExpression();
        return;
    }

	// 저장 후
	if ( sServiceName == "e02020020-service:saveRacerDegree" ) {
        return;
    }

	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

