/*-------------------------------------------------------------------------------------------------+
>>  추출조건설정
+-------------------------------------------------------------------------------------------------*/
function fcRacerCondProcess() {

    dsOutCond.ClearData();
    dsOutCond.AddColumn("RACER_GRD_CD");
    dsOutCond.AddColumn("COND");
    dsOutCond.AddColumn("COND1");
    dsOutCond.AddColumn("COND2");
    dsOutCond.AddColumn("COND3");
    dsOutCond.AddColumn("COND4");
    var nRow = 0;

    // 배정한도
    dsOutCond.AddRow();
    dsOutCond.SetColumn(nRow, "RACER_GRD_CD", "A1");
    dsOutCond.SetColumn(nRow, "COND"  , "20");
    dsOutCond.SetColumn(nRow, "COND2"  , "40");
    dsOutCond.SetColumn(nRow, "COND3"  , "0.8");
    dsOutCond.SetColumn(nRow, "COND4"  , "");
    nRow++;

    // 연대율
    dsOutCond.AddRow();
    dsOutCond.SetColumn(nRow, "RACER_GRD_CD", "A2");
    dsOutCond.SetColumn(nRow, "COND"  , "20");
    dsOutCond.SetColumn(nRow, "COND2"  , "30");
    dsOutCond.SetColumn(nRow, "COND3"  , "0.8");
    dsOutCond.SetColumn(nRow, "COND4"  , "");
    nRow++;

    // 평균사고점
    dsOutCond.AddRow();
    dsOutCond.SetColumn(nRow, "RACER_GRD_CD", "B1");
    dsOutCond.SetColumn(nRow, "COND"  , "50");
    dsOutCond.SetColumn(nRow, "COND2"  , "10");
    dsOutCond.SetColumn(nRow, "COND3"  , "0.8");
    dsOutCond.SetColumn(nRow, "COND4"  , "");
    nRow++;

    dsOutCond.AddRow();
    dsOutCond.SetColumn(nRow, "RACER_GRD_CD", "B2");
    dsOutCond.SetColumn(nRow, "COND"  , "");
    dsOutCond.SetColumn(nRow, "COND2"  , "");
    dsOutCond.SetColumn(nRow, "COND3"  , "");
    dsOutCond.SetColumn(nRow, "COND4"  , "");
    nRow++;

    fcSetRacerCnt();
}

/*-------------------------------------------------------------------------------------------------+
>>  출주수설정
+-------------------------------------------------------------------------------------------------*/
function fcSetRaceCnt() {
    // 출주횟수
    var i = 0;
    dsOutCond.SetColumn(i, "COND4"  , dsOutRacerGrdRaceCnt.GetColumn(i++, "STND_CNT_50"));
    dsOutCond.SetColumn(i, "COND4"  , dsOutRacerGrdRaceCnt.GetColumn(i++, "STND_CNT_50"));
    dsOutCond.SetColumn(i, "COND4"  , dsOutRacerGrdRaceCnt.GetColumn(i++, "STND_CNT_50"));
    dsOutCond.SetColumn(i, "COND4"  , "");
}

