/*-------------------------------------------------------------------------------------------------+
>>  상세성적조회
+-------------------------------------------------------------------------------------------------*/
function fcGetRankDetail() {
    if ( fnc_IsNull(dsOutRacerDegree.getColumn(dsOutRacerDegree.currow, "RACER_NO")) ) return;

    var sServiceName = "e02020030-service:searchRacerRankDetail";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutRacerRankDetail=dsOutRacerRankDetail ";

    fce_AddParam("RACER_NO" , dsOutRacerDegree.getColumn(dsOutRacerDegree.currow, "RACER_NO"));
    fce_AddParam("START_DAY", calSStrDt.value);
    fce_AddParam("END_DAY"  , calSEndDt.value);

    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

/*-------------------------------------------------------------------------------------------------+
>>  식표현
+-------------------------------------------------------------------------------------------------*/
function fcSetExpression()
{
    fnc_SetAllColumnValue(dsOutRacerSum, "DESC", "");

    var sAmuRankScr  = dsOutRacerRankDetail.Sum('RACE_SCR');
    var sAmuAcdntScr = dsOutRacerRankDetail.Sum('ACDNT_SCR');
    var sRaceCnt     = 0;
    var sRank1       = 0;
    var sRank2       = 0;

    var i = 0;
    var sRank = "";
    for ( i = 0; i < dsOutRacerRankDetail.GetRowCount(); i++ ) {
        sRank = dsOutRacerRankDetail.GetColumn(i, "RANK");
//        if ( sRank != "면책" && sRank != "결장" && sRank != "면책결장" ) {
        if ( sRank=="0" || sRank=="1" || sRank=="2" || sRank=="3" || sRank=="4" || sRank=="5" || sRank=="6" ) {
			sRaceCnt = sRaceCnt + 1;
			if ( sRank == "1" ) {
				sRank1 = sRank1 + 1;
			} else if ( sRank == "2" ) {
				sRank2 = sRank2 + 1;
			}
		}
    }

    i = 0;
    var sTemp = "";
    if ( sRaceCnt > 0 ) {
        // 평균착순점
        sTemp = sAmuRankScr 
              + " / " 
              + sRaceCnt 
              + " = " 
              + round(sAmuRankScr / sRaceCnt, 4);
        dsOutRacerSum.SetColumn(i++, "DESC", sTemp);

        // 승율
        sTemp = "( "
              + sRank1 
              + " ) / " 
              + sRaceCnt 
              + " × " 
              + 100 
              + " = " 
              + round((sRank1) / sRaceCnt * 100, 4);
        dsOutRacerSum.SetColumn(i++, "DESC", sTemp);

        // 연대율
        sTemp = "( "
              + sRank1 
              + " + " 
              + sRank2 
              + " ) / " 
              + sRaceCnt 
              + " × " 
              + 100 
              + " = " 
              + round((sRank1 + sRank2) / sRaceCnt * 100, 4);
        dsOutRacerSum.SetColumn(i++, "DESC", sTemp);    

        // 평균사고점
        sTemp = sAmuAcdntScr 
              + " / " 
              + sRaceCnt 
              + " = " 
              + round(sAmuAcdntScr / sRaceCnt, 4);
        dsOutRacerSum.SetColumn(i++, "DESC", sTemp);

        // 평균득점
        sTemp = "( "
              + sAmuRankScr 
              + " - " 
              + sAmuAcdntScr 
              + " ) / " 
              + sRaceCnt 
              + " = " 
              + round((sAmuRankScr - sAmuAcdntScr) / sRaceCnt, 4);
        dsOutRacerSum.SetColumn(i++, "DESC", sTemp);
    }
}

/*-------------------------------------------------------------------------------------------------+
>>  추출선수설정
+-------------------------------------------------------------------------------------------------*/
function fcSetRacerCnt()
{
    var nWorkRow = 4;

    var nStrPerio = cboStrPerio.Value;
    var nEndPerio = cboEndPerio.Value;

    var nRacerCnt = 0;
    for ( var i = nStrPerio; i <= nEndPerio; i++ ) {
        var nRow = fnc_GetExistValue(dsOutPerio, "RACER_PERIO_NO", i);
        nRacerCnt = nRacerCnt + toInteger(dsOutPerio.GetColumn(nRow, "PERIO_CNT"));
    }

    meRacerCnt.Value = nRacerCnt;

    for ( var i = 0; i < dsOutCond.GetRowCount(); i++ ) {
        dsOutCond.SetColumn(i, "COND1", Truncate(toInteger(meRacerCnt.Value) * toInteger(dsOutCond.GetColumn(i, "COND")) / 100));
    }
}

/*-------------------------------------------------------------------------------------------------+
>>  추출조건변경시
+-------------------------------------------------------------------------------------------------*/
function dsOutCond_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
    if ( strColumnID == "COND" ) {
        dsOutCond.SetColumn(nRow, "COND1", Truncate(toInteger(meRacerCnt.Value) * toInteger(dsOutCond.GetColumn(nRow, "COND")) / 100));
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  기준인원변경시
+-------------------------------------------------------------------------------------------------*/
function meRacerCnt_OnChanged(obj,strText)
{
    for ( var i = 0; i < dsOutCond.GetRowCount(); i++ ) {
        dsOutCond.SetColumn(i, "COND1", Truncate(toInteger(meRacerCnt.Value) * toInteger(dsOutCond.GetColumn(i, "COND")) / 100));
    }
}
