	//자료 변경 여부를 체크하고자 하는  DataSet Object를 넘긴다.
	var sDataset = "dsOutEscRacer";
	if ( !fnc_FormUnloadCheck(sDataset , sFormId) ) return false;
}

/*-------------------------------------------------------------------------------------------------+
>>  단축키 처리(Form KeyDown)
+-------------------------------------------------------------------------------------------------*/
function fcHotKey(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {

	//Return Key Press時 Next Component focus
	if ( nChar == 13 ) {
		var oFocusComponent = GetNextComponent(true);
		oFocusComponent.setfocus();
		return;
	}

	//Control Key가 눌려져 있다면 단축키 처리
	//if (bControl) fnc_HotKey(this, nChar);
}

/***************************************************************************************************
*                                      공통 버튼 Event 처리 부분                                   *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
    if ( obj.ID == "btnSearch" ) {
        dsOutRacerGrd.ClearData();
        dsOutRacer.ClearData();
        return;
    }

    if ( obj.ID == "btnExec" ) {
        dsOutEscRacer.ClearData();
        return;
    }

    if ( obj.ID == "btnCancel" ) {
        fcRecdArrangeEscStndProcess();
    }

    dsOutRacer.ClearData();
    dsOutRacerGrd.ClearData();
    dsOutPerio.ClearData();
    dsOutEscRacer.ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);

    if( !fnc_IsMandatory(calSStrDt, "시작일") ) return false;
    if( !fnc_IsMandatory(calSEndDt, "종료일") ) return false;
    if( !fnc_IsMandatory(meOpenDay, "개최일수") ) return false;

    if( toInteger(meOpenDay.Value) <= 0 ) {
        fnc_message(L_COM_ALT_0024, "N", "개최일은 0보다 작을 수");
        return false;
    }

    if ( calSStrDt.Value > calSEndDt.Value ) {
        fnc_message(L_COM_ALT_0024, "N", "시작일이 종료일 이후일 수");
        return false;
    }

	return true;
}

function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "e02030020-service:searchRecdArrangeEscStnd";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutRacerGrd=dsOutRacerGrd "
                     + "dsOutPerio=dsOutPerio "
                     + "dsOutRacer=dsOutRacer ";

	fce_AddParam(Trim("STND_YEAR       "), cboSStndYear.Value        );
	fce_AddParam(Trim("QURT_CD         "), cboSQurt.Value            );
	fce_AddParam(Trim("STR_DT          "), calSStrDt.Value           );
	fce_AddParam(Trim("END_DT          "), calSEndDt.Value           );
	fce_AddParam(Trim("RACER_GRD_STR_DT"), calSRacerGrdStrDt.Value   );
	fce_AddParam(Trim("RACER_GRD_END_DT"), calSRacerGrdEndDt.Value   );
	fce_AddParam(Trim("STR_PERIO       "), cboStrPerio.Value         );
	fce_AddParam(Trim("END_PERIO       "), cboEndPerio.Value         );
	fce_AddParam(Trim("OPEN_DAY        "), meOpenDay.Value           );

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

