/*-------------------------------------------------------------------------------------------------+
>>  실행 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchExecCheck(obj) {

	fcInit(obj);

	// 필수항목체크
    for ( var i = 0; i < dsOutRecdArrangeEscStnd.GetRowCount(); i++ ) {
        var sStrRacerPerioNo = dsOutRecdArrangeEscStnd.GetColumn(i, "STR_RACER_PERIO_NO");
        var sEndRacerPerioNo = dsOutRecdArrangeEscStnd.GetColumn(i, "END_RACER_PERIO_NO");
        var sStrCond         = dsOutRecdArrangeEscStnd.GetColumn(i, "STR_COND");
        var sEndCond         = dsOutRecdArrangeEscStnd.GetColumn(i, "END_COND");

        switch ( dsOutRecdArrangeEscStnd.GetColumn(i, "RECD_ESC_CLS_CD") ) {
            case "201" :
                if ( !fcIsMandatory(sStrRacerPerioNo, "시작기수", i) ) return false;
                if ( !fcIsMandatory(sEndRacerPerioNo, "종료기수", i) ) return false;
                if ( !fcIsMandatory(sStrCond        , "조건1"   , i) ) return false;
                break;
            case "202" :
                if ( !fcIsMandatory(sStrRacerPerioNo, "시작기수", i) ) return false;
                if ( !fcIsMandatory(sEndRacerPerioNo, "종료기수", i) ) return false;
                if ( !fcIsMandatory(sStrCond        , "조건1"   , i) ) return false;
                break;
            case "203" :
                if ( !fcIsMandatory(sStrRacerPerioNo, "시작기수", i) ) return false;
                if ( !fcIsMandatory(sEndRacerPerioNo, "종료기수", i) ) return false;
                if ( !fcIsMandatory(sStrCond        , "조건1"   , i) ) return false;
                if ( !fcIsMandatory(sEndCond        , "조건2"   , i) ) return false;
                break;
            case "204" :
                if ( !fcIsMandatory(sStrRacerPerioNo, "시작기수", i) ) return false;
                if ( !fcIsMandatory(sEndRacerPerioNo, "종료기수", i) ) return false;
                if ( !fcIsMandatory(sStrCond        , "조건1"   , i) ) return false;
                break;
            case "205" :
                if ( !fcIsMandatory(sStrRacerPerioNo, "시작기수", i) ) return false;
                if ( !fcIsMandatory(sEndRacerPerioNo, "종료기수", i) ) return false;
                if ( !fcIsMandatory(sStrCond        , "조건1"   , i) ) return false;
                break;
        }
    }

	dsOutRacerListEsc.Clear();
	dsOutRacerListEsc.AddColumn("RACER_NO");
	var nRow = 0;

	for ( var i = 0; i < dsOutRacer.GetRowCount(); i++ ) {
        if ( dsOutRacer.GetColumn(i, "chk") == "1" ) {
            dsOutRacerListEsc.AddRow();
            dsOutRacerListEsc.SetColumn(nRow, "RACER_NO", dsOutRacer.GetColumn(i, "RACER_NO"));
            nRow++;
        }
	}

	if ( dsOutRacerGrd.GetRowCount() <= 0 ) {
        fnc_message(L_COM_CNF_0000, "N", "조회 후 실행하시기 바랍니다.");
        return false;
	}

	return true;
}

function fcSearchExec(obj) {
    if ( !fcSearchExecCheck(obj) ) return;

	var sServiceName = "e02030020-service:searchEscRacer";
    var sInDataSet   = "dsOutRecdArrangeEscStnd=dsOutRecdArrangeEscStnd "
                     + "dsOutRacerListEsc=dsOutRacerListEsc ";
    var sOutDataSet  = "dsOutEscRacer=dsOutEscRacer ";

	fce_AddParam(Trim("STND_YEAR"       ), cboSStndYear.Value);
	fce_AddParam(Trim("QURT_CD"         ), cboSQurt.Value    );
	fce_AddParam(Trim("STR_DT"          ), calSStrDt.Value   );
	fce_AddParam(Trim("END_DT"          ), calSEndDt.Value   );
	fce_AddParam(Trim("RACER_GRD_STR_DT"), calSRacerGrdStrDt.Value   );
	fce_AddParam(Trim("RACER_GRD_END_DT"), calSRacerGrdEndDt.Value   );
	fce_AddParam(Trim("STR_PERIO       "), cboStrPerio.Value         );
	fce_AddParam(Trim("END_PERIO       "), cboEndPerio.Value         );
	fce_AddParam(Trim("OPEN_DAY        "), meOpenDay.Value           );

	var nRow = 0;
	fce_AddParam(Trim("A1_STND_CNT_50   "), dsOutRacerGrd.GetColumn(nRow++, "STND_CNT_50"));
	fce_AddParam(Trim("A2_STND_CNT_50   "), dsOutRacerGrd.GetColumn(nRow++, "STND_CNT_50"));
	fce_AddParam(Trim("B1_STND_CNT_50   "), dsOutRacerGrd.GetColumn(nRow++, "STND_CNT_50"));
	fce_AddParam(Trim("B2_STND_CNT_50   "), dsOutRacerGrd.GetColumn(nRow++, "STND_CNT_50"));


	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutRecdArrangeEscStnd) )
	{
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}

	// 필수항목체크
    for ( var i = 0; i < dsOutRecdArrangeEscStnd.GetRowCount(); i++ ) {
        var sStrRacerPerioNo = dsOutRecdArrangeEscStnd.GetColumn(i, "STR_RACER_PERIO_NO");
        var sEndRacerPerioNo = dsOutRecdArrangeEscStnd.GetColumn(i, "END_RACER_PERIO_NO");
        var sStrCond         = dsOutRecdArrangeEscStnd.GetColumn(i, "STR_COND");
        var sEndCond         = dsOutRecdArrangeEscStnd.GetColumn(i, "END_COND");

        switch ( dsOutRecdArrangeEscStnd.GetColumn(i, "RECD_ESC_CLS_CD") ) {
            case "201" :
                if ( !fcIsMandatory(sStrRacerPerioNo, "시작기수", i) ) return false;
                if ( !fcIsMandatory(sEndRacerPerioNo, "종료기수", i) ) return false;
                if ( !fcIsMandatory(sStrCond        , "조건1"   , i) ) return false;
                break;
            case "202" :
                if ( !fcIsMandatory(sStrRacerPerioNo, "시작기수", i) ) return false;
                if ( !fcIsMandatory(sEndRacerPerioNo, "종료기수", i) ) return false;
                if ( !fcIsMandatory(sStrCond        , "조건1"   , i) ) return false;
                break;
            case "203" :
                if ( !fcIsMandatory(sStrRacerPerioNo, "시작기수", i) ) return false;
                if ( !fcIsMandatory(sEndRacerPerioNo, "종료기수", i) ) return false;
                if ( !fcIsMandatory(sStrCond        , "조건1"   , i) ) return false;
                if ( !fcIsMandatory(sEndCond        , "조건2"   , i) ) return false;
                break;
            case "204" :
                if ( !fcIsMandatory(sStrRacerPerioNo, "시작기수", i) ) return false;
                if ( !fcIsMandatory(sEndRacerPerioNo, "종료기수", i) ) return false;
                if ( !fcIsMandatory(sStrCond        , "조건1"   , i) ) return false;
                break;
            case "205" :
                if ( !fcIsMandatory(sStrRacerPerioNo, "시작기수", i) ) return false;
                if ( !fcIsMandatory(sEndRacerPerioNo, "종료기수", i) ) return false;
                if ( !fcIsMandatory(sStrCond        , "조건1"   , i) ) return false;
                break;
        }
    }

	if ( dsOutEscRacer.GetRowCount() == 0 ) {
        //if ( !fnc_Message(SNIS_BOA_B001, "Y", cboSStndYear.Value, cboSQurt.Text) ) return false;
	}

	if ( dsOutEscRacer.GetRowCount() > 0 ) {
        if ( fnc_IsMandatory(calStrDt, "시작일자") ) return false;
        if ( fnc_IsMandatory(calEndDt, "종료일자") ) return false;
	}

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  필수입력사항
+-------------------------------------------------------------------------------------------------*/
function fcIsMandatory(controlValue, controlName, nRow)
{
    if ( fnc_IsNull(controlValue) )
    {
        fnc_Message(SNIS_BOA_0001, "N", i + 1, controlName); 
        dsOutRecdArrangeEscStnd.row = nRow;
        grdRecdArrangeEscStnd.SetFocus();
        return false;
    }
    return true;
}

function fcSave(obj) {
    fcSaveEscArragne(obj);
}

/*-------------------------------------------------------------------------------------------------+
>>  주선제외자등록
+-------------------------------------------------------------------------------------------------*/
function fcSaveEscArragneCheck(obj) {

    if ( !fnc_IsMandatory(calStrDt, "시작일") ) return false;
    if ( !fnc_IsMandatory(calEndDt, "종료일") ) return false;

	fnc_CopyDataset(dsOutTemp, dsOutEscRacer, "chk", 1);
    if ( dsOutTemp.GetRowCount() == 0 ) {
        fnc_Message(SNIS_COM_1006, "N");
        return false;
    }

    return true;
}

function fcSaveEscArragne(obj) {

    if ( !fcSaveEscArragneCheck(obj) ) return;

	var sServiceName = "e02030020-service:saveRecdArrangeEscStnd";
    var sInDataSet   = "dsOutEscRacer=dsOutTemp ";
    var sOutDataSet  = "";

	fce_AddParam(Trim("STND_YEAR"), cboSStndYear.Value);
	fce_AddParam(Trim("QURT_CD"  ), cboSQurt.Value    );
	fce_AddParam(Trim("STR_DT"   ), calStrDt.Value    );
	fce_AddParam(Trim("END_DT"   ), calEndDt.Value    );

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

