/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_Result(ErrorCode, ErrorMSG) ) return;

	// 공통코드 조회시
	if ( sServiceName == "common-service:searchCode" ) {
        cboEndPerio.Index = dsOutRacerPerioNo.GetRowCount() - 1;
        fcRecdArrangeEscStndProcess();
		fcSearchRecdStndTerm();
		cboSQurt.Index = 0;
		//fcSearch();
		return;
	}

	// 조회 후
	if ( sServiceName == "e02010040-service:searchRecdStndTerm" ) {
        cboSQurt_OnChanged();
        return;
	}

	// 조회 후
	if ( sServiceName == "e02030020-service:searchRecdArrangeEscStnd" ) {
        dsOutRacer.AddColumn("chk");
        setEscRacerCnt();
        return;
	}

	// 조회 후
	if ( sServiceName == "e02030020-service:searchEscRacer" ) {
        dsOutEscRacer.AddColumn("chk");
        dsOutEscRacer.AddColumn("DESC");
        fnc_SetAllColumnValue(dsOutEscRacer, "chk", 1);

        for ( var i = 0; i < dsOutEscRacer.GetRowCount(); i++ ) {
            var nRow = fnc_GetExistValue(dsOutRecdArrangeEscStnd, "RECD_ESC_CLS_CD", dsOutEscRacer.GetColumn(i, "RSN_CD"));
            dsOutEscRacer.SetColumn(i, "DESC", dsOutRecdArrangeEscStnd.GetColumn(nRow, "COND"));
        }

        return;
	}

	// 저장 후
	if ( sServiceName == "e02030020-service:saveRecdArrangeEscStnd" ) {

		return;
	}

	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

/*-------------------------------------------------------------------------------------------------+
>>  추출조건설정
+-------------------------------------------------------------------------------------------------*/
function fcRecdArrangeEscStndProcess() {

    dsOutRecdArrangeEscStnd.ClearData();
    dsOutRecdArrangeEscStnd.AddColumn("COND");
    dsOutRecdArrangeEscStnd.AddColumn("COND_ORG");
    var nRow = 0;

   	//출발위반
    dsOutRecdArrangeEscStnd.AddRow();
    dsOutRecdArrangeEscStnd.SetColumn(nRow, "RECD_ESC_CLS_CD", "204");
    dsOutRecdArrangeEscStnd.SetColumn(nRow, "STR_RACER_PERIO_NO", "1");
    dsOutRecdArrangeEscStnd.SetColumn(nRow, "END_RACER_PERIO_NO", dsOutRacerPerioNo.GetRowCount() - 2);
    dsOutRecdArrangeEscStnd.SetColumn(nRow, "STR_COND", "2");
    dsOutRecdArrangeEscStnd.SetColumn(nRow, "COND", "_ ~ _ 기수 선수중 출발위반 연간 _회 이상");
    dsOutRecdArrangeEscStnd.SetColumn(nRow, "COND_ORG", "_ ~ _ 기수 선수중 출발위반 연간 _회 이상");
    nRow++;

    // 평균사고점
    dsOutRecdArrangeEscStnd.AddRow();
    dsOutRecdArrangeEscStnd.SetColumn(nRow, "RECD_ESC_CLS_CD", "202");
    dsOutRecdArrangeEscStnd.SetColumn(nRow, "STR_RACER_PERIO_NO", "1");
    dsOutRecdArrangeEscStnd.SetColumn(nRow, "END_RACER_PERIO_NO", dsOutRacerPerioNo.GetRowCount() - 2);
    dsOutRecdArrangeEscStnd.SetColumn(nRow, "STR_COND", "1.5");
    dsOutRecdArrangeEscStnd.SetColumn(nRow, "COND", "_ ~ _ 기수 선수중 평균 사고점 _점 이상");
    dsOutRecdArrangeEscStnd.SetColumn(nRow, "COND_ORG", "_ ~ _ 기수 선수중 평균 사고점 _점 이상");
    nRow++;

    // 평균사고점 연속불량
    dsOutRecdArrangeEscStnd.AddRow();
    dsOutRecdArrangeEscStnd.SetColumn(nRow, "RECD_ESC_CLS_CD", "203");
    dsOutRecdArrangeEscStnd.SetColumn(nRow, "STR_RACER_PERIO_NO", "1");
    dsOutRecdArrangeEscStnd.SetColumn(nRow, "END_RACER_PERIO_NO", dsOutRacerPerioNo.GetRowCount() - 2);
    dsOutRecdArrangeEscStnd.SetColumn(nRow, "STR_COND", "0.80");
    dsOutRecdArrangeEscStnd.SetColumn(nRow, "END_COND", "3");
    dsOutRecdArrangeEscStnd.SetColumn(nRow, "COND", "_ ~ _ 기수 선수중 평균사고점 _이상 _회연속 ");
    dsOutRecdArrangeEscStnd.SetColumn(nRow, "COND_ORG", "_ ~ _ 기수 선수중 평균사고점 _이상 _회연속 ");
    nRow++;

    //성적불량 최하위
    dsOutRecdArrangeEscStnd.AddRow();
    dsOutRecdArrangeEscStnd.SetColumn(nRow, "RECD_ESC_CLS_CD", "205");
    dsOutRecdArrangeEscStnd.SetColumn(nRow, "STR_RACER_PERIO_NO", "1");
    dsOutRecdArrangeEscStnd.SetColumn(nRow, "END_RACER_PERIO_NO", dsOutRacerPerioNo.GetRowCount() - 2);
    dsOutRecdArrangeEscStnd.SetColumn(nRow, "STR_COND", "5");
    dsOutRecdArrangeEscStnd.SetColumn(nRow, "COND", "_ ~ _ 기수 선수중 경주성적불량 최하위 _%");
    dsOutRecdArrangeEscStnd.SetColumn(nRow, "COND_ORG", "_ ~ _ 기수 선수중 경주성적불량 최하위 _%");
    nRow++;

    // 평균착순점
    dsOutRecdArrangeEscStnd.AddRow();
    dsOutRecdArrangeEscStnd.SetColumn(nRow, "RECD_ESC_CLS_CD", "201");
    dsOutRecdArrangeEscStnd.SetColumn(nRow, "STR_RACER_PERIO_NO", "1");
    dsOutRecdArrangeEscStnd.SetColumn(nRow, "END_RACER_PERIO_NO", dsOutRacerPerioNo.GetRowCount() - 2);
    dsOutRecdArrangeEscStnd.SetColumn(nRow, "STR_COND", "3.0");
    dsOutRecdArrangeEscStnd.SetColumn(nRow, "COND", "_ ~ _ 기수 선수중 평균 착순점이 _점 이하");
    dsOutRecdArrangeEscStnd.SetColumn(nRow, "COND_ORG", "_ ~ _ 기수 선수중 평균 착순점이 _점 이하");
    nRow++;



    fcDisplayCond();
    dsOutRecdArrangeEscStnd.UpdateControl = true;
    dsOutRecdArrangeEscStnd.FireEvent = true;
}

