/*-------------------------------------------------------------------------------------------------+
>>  Column값이 변경된 경우
+-------------------------------------------------------------------------------------------------*/
function dsOutRecdArrangeEscStnd_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if ( strColumnID == "STR_RACER_PERIO_NO" 
	  || strColumnID == "END_RACER_PERIO_NO" 
	  || strColumnID == "STR_COND" 
	  || strColumnID == "END_COND" 
	  ) {
        fcDisplayCond();
	}

	if ( strColumnID == "STR_RACER_PERIO_NO" 
	  || strColumnID == "END_RACER_PERIO_NO" 
	  || strColumnID == "STR_COND" 
	  || strColumnID == "END_COND" 
	  || strColumnID == "BF_ESC_RACER_CNT" 
	  ) {
        if ( nRow == 3 ) setEscRacerCnt();
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  화면에 조건을 보여줌
+-------------------------------------------------------------------------------------------------*/
function fcDisplayCond() {
    for ( var i = 0; i < dsOutRecdArrangeEscStnd.GetRowCount(); i++ ) {
        var sStrRacerPerioNo = dsOutRecdArrangeEscStnd.GetColumn(i, "STR_RACER_PERIO_NO");
        var sEndRacerPerioNo = dsOutRecdArrangeEscStnd.GetColumn(i, "END_RACER_PERIO_NO");
        var sStrCond         = dsOutRecdArrangeEscStnd.GetColumn(i, "STR_COND");
        var sEndCond         = dsOutRecdArrangeEscStnd.GetColumn(i, "END_COND");
        var sCond            = dsOutRecdArrangeEscStnd.GetColumn(i, "COND_ORG");
        var sParam           = "_";

        switch ( dsOutRecdArrangeEscStnd.GetColumn(i, "RECD_ESC_CLS_CD") ) {
            case "201" :
                dsOutRecdArrangeEscStnd.SetColumn(i, "COND", fcSetArgument(sCond, sParam, sStrRacerPerioNo, sEndRacerPerioNo, sStrCond));
                break;
            case "202" :
                dsOutRecdArrangeEscStnd.SetColumn(i, "COND", fcSetArgument(sCond, sParam, sStrRacerPerioNo, sEndRacerPerioNo, sStrCond));
                break;
            case "203" :
                dsOutRecdArrangeEscStnd.SetColumn(i, "COND", fcSetArgument(sCond, sParam, sStrRacerPerioNo, sEndRacerPerioNo, sStrCond, sEndCond));
                break;
            case "204" :
                dsOutRecdArrangeEscStnd.SetColumn(i, "COND", fcSetArgument(sCond, sParam, sStrRacerPerioNo, sEndRacerPerioNo, sStrCond));
                break;
            case "205" :
                dsOutRecdArrangeEscStnd.SetColumn(i, "COND", fcSetArgument(sCond, sParam, sStrRacerPerioNo, sEndRacerPerioNo, sStrCond));
                break;
        }
    }
}

/*-------------------------------------------------------------------------------------------------+
>>  인자 변경
+-------------------------------------------------------------------------------------------------*/
function fcSetArgument(sValue, sParam, sArg1, sArg2, sArg3, sArg4, sArg5) {

	var sReturn  = "";
	var arSplit  = array();
	var nInner   = 1;
	var sEval    = "";

	arSplit = split(sValue, sParam);

	for ( var i = 0; i < arSplit.Length(); i++ ) {

        if ( i = 0 ) {
            sReturn = arSplit[i];
            continue;
        }

	    if ( fnc_IsNull(eval("sArg" + nInner)) ) {
            sReturn = sReturn + sParam + arSplit[i];
	    } else {
	        sReturn = sReturn + eval("sArg" + nInner) + arSplit[i];
	    }

        nInner++;
	}

	return sReturn;
}

