/*-------------------------------------------------------------------------------------------------+
>>  성적불량인원산출
+-------------------------------------------------------------------------------------------------*/
function setEscRacerCnt()
{
    var nWorkRow = 3;

    var nStrPerio = dsOutRecdArrangeEscStnd.GetColumn(nWorkRow, "STR_RACER_PERIO_NO");
    var nEndPerio = dsOutRecdArrangeEscStnd.GetColumn(nWorkRow, "END_RACER_PERIO_NO");

    var nRacerCnt = 0;
    for ( var iCnt = nStrPerio; iCnt <= toNumber(nEndPerio); iCnt++ ) {

        var nRow = dsOutPerio.FindRow("RACER_PERIO_NO", iCnt);
        //var nRow = fnc_GetExistValue(dsOutPerio, "RACER_PERIO_NO", i);
        if (nRow >= 0) {
			//nRacerCnt = nRacerCnt + toInteger(dsOutPerio.GetColumn(nRow, "ESC_TOT_CNT") + dsOutPerio.GetColumn(nRow, "USE_CNT"));
        	var nRegCnt = toNumber(dsOutPerio.GetColumn(nRow, "REG_CNT"));

        	nRacerCnt = nRacerCnt + toInteger(dsOutPerio.GetColumn(nRow, "ESC_TOT_CNT") + dsOutPerio.GetColumn(nRow, "USE_CNT"));
        }
    }

    var nBfEscRacerCnt = toInteger(dsOutRecdArrangeEscStnd.GetColumn(nWorkRow, "BF_ESC_RACER_CNT"));
    var nEscRacerCnt   = Truncate((nRacerCnt - nBfEscRacerCnt) * toNumber(dsOutRecdArrangeEscStnd.GetColumn(nWorkRow, "STR_COND")) / 100);

    dsOutRecdArrangeEscStnd.SetColumn(nWorkRow, "RACER_CNT"    , nRacerCnt   );
    dsOutRecdArrangeEscStnd.SetColumn(nWorkRow, "ESC_RACER_CNT", nEscRacerCnt);
}

