function btnSendMail_OnClick(obj)
{
	var sMailGbn = "";

	//메일발송요청일자 오늘날짜 보다 큰지 체크 
    if(ToInteger(calSendReqDt.Value)-ToInteger(CURR_DT) < 0){
		fnc_Message(SNIS_BOA_G005);
		return;
	}

    if (obj.id == "btnSendMail") sMailGbn = "S";
    else if (obj.id == "btnAddMail") sMailGbn = "A";

	mailSendReq(sMailGbn);
}

function mailSendReq(sMailGbn) {
	var sServiceName = "e02030035-service:saveArrangeNoti";
    var sInDataSet   = "dsOutArrangeNotiList=dsOutArrangeNotiList ";
    var sOutDataSet  = "dsOutArrangeNoti=dsOutArrangeNoti ";

	fce_AddParam("MAIL_GBN",			sMailGbn);
	fce_AddParam("STND_YEAR",			dsOutArrangeNoti.GetColumn(dsOutArrangeNoti.rowposition, 'STND_YEAR'));
	fce_AddParam("MBR_CD",				dsOutArrangeNoti.GetColumn(dsOutArrangeNoti.rowposition, 'MBR_CD'));
	fce_AddParam("TMS",					edTms.Text);
	fce_AddParam("SEQ",					dsOutArrangeNoti.GetColumn(dsOutArrangeNoti.rowposition, 'SEQ'));
	fce_AddParam("MEET_DT",				calMeetDt.Value);
	fce_AddParam("MEET_MM",				mskMeetMm.Value);
	fce_AddParam("REPORT_DT",			calReportDt.Value);
	fce_AddParam("REPORT_MM",			mskReportMm.Value);
	fce_AddParam("MAIL_SEND_REQ_DAY",	calSendReqDt.Value);
	fce_AddParam("NOTICE",				taNotice.Text);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

