/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_Result(ErrorCode, ErrorMSG) ) return;

	// 공통코드 조회시
	if ( sServiceName == "common-service:searchCode" ) {
		cboSMbr.Index = 0;
		return;
	}

	// 조회 후
	if ( sServiceName == "e02030035-service:searchArrangeNoti" ) {
		return;
	}

	if ( sServiceName == "e02030035-service:searchArrangeNotiList" 
	  || sServiceName == "e02030035-service:searchArrangeNotiListAdd" ) {
		var sMYear = dsOutArrangeNoti.GetColumn(dsOutArrangeNoti.row, "STND_YEAR");
		var sMTms  = dsOutArrangeNoti.GetColumn(dsOutArrangeNoti.row, "TMS");
		var sMSeq  = dsOutArrangeNoti.GetColumn(dsOutArrangeNoti.row, "SEQ");

		for (i=0; i< dsOutArrangeNotiList.rowcount; i++) {
			dsOutArrangeNotiList.SetColumn(i, "MAPPING_YEAR", NToken(dsOutArrangeNotiList.GetColumn(i, "MAPPING"), '|', 1));
			dsOutArrangeNotiList.SetColumn(i, "MAPPING_TMS", NToken(dsOutArrangeNotiList.GetColumn(i, "MAPPING"), '|', 2));
			dsOutArrangeNotiList.SetColumn(i, "MAPPING_SEQ", NToken(dsOutArrangeNotiList.GetColumn(i, "MAPPING"), '|', 3));

			if ( (dsOutArrangeNotiList.GetColumn(i, "MAPPING") != "NULL") 
			  && (dsOutArrangeNotiList.GetColumn(i, "MAPPING") != "")
			  && ((dsOutArrangeNotiList.GetColumn(i, "MAPPING_YEAR") != sMYear) || (dsOutArrangeNotiList.GetColumn(i, "MAPPING_TMS") != sMTms) || (dsOutArrangeNotiList.GetColumn(i, "MAPPING_SEQ") != sMSeq)) )
					dsOutArrangeNotiList.SetColumn(i, "CHK", "0");
		}
		dsOutArrangeNotiList.Filter("MAPPING=NULL||MAPPING=''||(MAPPING_YEAR='"+ sMYear +"'&&MAPPING_TMS='"+ sMTms+ "'&&MAPPING_SEQ='"+ sMSeq +"')");
		return;
	}

	// 메일발송요청 후
	if ( sServiceName == "e02030035-service:saveArrangeNoti" ) {
		fcSearch();
		return;
	}

	// 저장 후
	if ( sServiceName == "e02030035-service:saveArrangeNotiList" ) {
		fcSearch();
		return;
	}

	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

