function dsOutArrangeNoti_OnRowPosChanged(obj,nOldRow,nRow)
{
	edTms.Text = dsOutArrangeNoti.GetColumn(nRow, "TMS");
	if ( dsOutArrangeNoti.GetColumn(nRow, "MAIL_SEND_FLAG") == 'Y') {
		btnSendMail.Enable = false;
		btnAddMail.Enable = true;
		grdArrangeNotiList.Editable = true;
		btnSendMail.Text = "주선통보";
	} else if ( dsOutArrangeNoti.GetColumn(nRow, "MAIL_SEND_FLAG") == 'N') {
		btnSendMail.Enable = true;
		btnAddMail.Enable = false;
		grdArrangeNotiList.Editable = true;
		btnSendMail.Text = "수정발송";
	} else {
		btnSendMail.Enable = true;
		btnAddMail.Enable = false;
		grdArrangeNotiList.Editable = false;  // (2010.3.6)처음 주선통보시에도 전체가 아닌 선별 발송 가능토록 요청
		//grdArrangeNotiList.Editable = true;
		btnSendMail.Text = "주선통보";
	}

	var sServiceName = "e02030035-service:searchArrangeNotiList";

	if ( dsOutArrangeNoti.GetColumn(nRow, "SEQ") > 1 ) {
		btnAddMail.Enable = false;
		sServiceName = "e02030035-service:searchArrangeNotiListAdd";
	}
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutArrangeNotiList=dsOutArrangeNotiList ";

	fce_AddParam(Trim("STND_YEAR"), dsOutArrangeNoti.GetColumn(nRow, "STND_YEAR") );
	fce_AddParam(Trim("MBR_CD   "), dsOutArrangeNoti.GetColumn(nRow, "MBR_CD") );
	fce_AddParam(Trim("TMS      "), dsOutArrangeNoti.GetColumn(nRow, "TMS") );
	fce_AddParam(Trim("SEQ      "), dsOutArrangeNoti.GetColumn(nRow, "SEQ") );
	fce_AddParam(Trim("WORKDAY  "), dsOutArrangeNoti.GetColumn(nRow, "EMS_WORK_DAY") );
	//fce_AddParam(Trim("EMS_SEQ  "), dsOutArrangeNoti.GetColumn(nRow, "EMS_SEQ_NO") );

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

function cbxAll_OnClick(obj,strValue)
{
	if ( cbxAll.Value ) 
		for (i=0; i< dsOutArrangeNotiList.rowcount; i++)	dsOutArrangeNotiList.SetColumn(i, "CHK", "1");
	else
		for (i=0; i< dsOutArrangeNotiList.rowcount; i++)	dsOutArrangeNotiList.SetColumn(i, "CHK", "0");
}


// SMS 전송 이벤트
function btnSmsSend_OnClick(obj)
{
	var sParams = "";
	var sStnd_year = dsOutArrangeNoti.GetColumn(dsOutArrangeNoti.currow, "STND_YEAR");
	var sTms = dsOutArrangeNoti.GetColumn(dsOutArrangeNoti.currow, "TMS");

	var sSendPhoneNumber 	= gdsUser.GetColumn(0,"TEL_NO"); //송신자 사무실 전화번호
	var sSendUserId 	 	= gdsUser.GetColumn(0,"USER_ID");//송신자 사번
	var sSendUserName 		= gdsUser.GetColumn(0,"USER_NM");//송신자명 
	var sReceivePhoneNumber = "";							 //수신자이동전화번호 , 여러명인경우 ; 로 구분 
	var sReceiveName 		= "";							 //수신자이동전화번호 , 여러명인경우 ; 로 구분 
	var sReceiveId          = "";
	var sTitle              = "선수주선통보 문자 발송";
	var sContents			= dsOutArrangeNoti.GetColumn(dsOutArrangeNoti.currow, "NOTICE");


	if(sStnd_year == null or sTms == null) {
		fnc_Message("년도 또는 회차정보가 없습니다. 확인하시고 문자전송 버튼을 클릭하시기 바랍니다.", "N");
		return;
	}

	for(var i = 0; i <dsOutArrangeNotiList.GetRowCount();i++){
		sReceivePhoneNumber +=  dsOutArrangeNotiList.GetColumn(i,"CELPON_NO")+";";
		sReceiveName        +=  dsOutArrangeNotiList.GetColumn(i,"RACER_NM")+";";
		sReceiveId          +=  dsOutArrangeNotiList.GetColumn(i,"RACER_NO")+";";
	}

	sReceivePhoneNumber = substr(sReceivePhoneNumber,0,LENGTH(sReceivePhoneNumber)-1);
	sReceiveName        = substr(sReceiveName,0,LENGTH(sReceiveName)-1);
	sReceiveId          = substr(sReceiveId,0,LENGTH(sReceiveId)-1);

	sParams  = " sSendPhoneNumber="+quote(sSendPhoneNumber);
	sParams += " sSendUserId="+quote(sSendUserId);
	sParams += " sSendUserName="+quote(sSendUserName);

	sParams += " sReceivePhoneNumber="+quote(sReceivePhoneNumber);
	sParams += " sReceiveName="+quote(sReceiveName);
	sParams += " sReceiveId="+quote(sReceiveId);
	sParams += " sTitle="+quote(sTitle);
	sParams += " sContents="+quote(sContents);
	sParams += " sFormId="+quote(sFormId);

	Dialog("common::comSendSms.xml", sParams);
}


