/*-------------------------------------------------------------------------------------------------+
>>  선수변경
+-------------------------------------------------------------------------------------------------*/
function btnRacerChng_OnClick(obj)
{
	if ( dsOutArrange.GetRowCount() == 0 ) return;

	// if ( fnc_IsNull(dsOutArrange.GetColumn(dsOutArrange.currow, "RACER_NO")) ) {
        // return;
	// }

	var sArg = " Arg_sStndYear=" + quote(dsOutArrange.GetColumn(dsOutArrange.currow, "STND_YEAR"))
	         + " Arg_sMbrCd="    + quote(dsOutArrange.GetColumn(dsOutArrange.currow, "MBR_CD"   ))
	         + " Arg_sTms="      + quote(dsOutArrange.GetColumn(dsOutArrange.currow, "TMS"      ))
	         + " Arg_sDayOrd="   + quote(""                                                      )
	         + " Arg_sRaceNo="   + quote(""                                                      )
	         + " Arg_sRacerNo="  + quote(dsOutArrange.GetColumn(dsOutArrange.currow, "RACER_NO" ))
	         + " Arg_sRacerRsn=" + quote(dsOutArrange.GetColumn(dsOutArrange.currow, "RACER_RSN"));

    var bReturn = Dialog("form::E02040011.xml", sArg, 700, 360, "");

    if ( bReturn == false || fnc_Isnull(bReturn) ) return;
    dsOutArrange.SetColumn(dsOutArrange.currow, "RACER_NO" , bReturn[0]);
    dsOutArrange.SetColumn(dsOutArrange.currow, "RACER_RSN", bReturn[1]);
    fcSearchCurrRow();
}

