/*-------------------------------------------------------------------------------------------------+
>>  금회출주변경(Grid Double Click시)
+-------------------------------------------------------------------------------------------------*/
function grdArrange_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if ( nCell >= 7  && nCell <= 13 ) {
        var sColumnNm = grdArrange.GetCellProp("body", nCell, "ColId");
        if ( toInteger(dsOutArrange.GetColumn(nRow, sColumnNm)) < 2 ) {
            dsOutArrange.SetColumn(nRow, sColumnNm, toInteger(dsOutArrange.GetColumn(nRow, sColumnNm)) + 1);
        } else {
            dsOutArrange.SetColumn(nRow, sColumnNm, "0");
        }
        return;
	}
}

