/*-------------------------------------------------------------------------------------------------+
>>  Transaction ÈÄ Ã³¸® ÇØ¾ß ÇÒ ³»¿ë!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_Result(ErrorCode, ErrorMSG) ) return;

	// °øÅëÄÚµå Á¶È¸½Ã
	if ( sServiceName == "common-service:searchCode" ) {
	    fce_AddCodeRow(dsOutRacerGrd, "ÀüÃ¼", "", cboSRacerGrd, false);
        fce_SetStndYear(dsOutSStndYear, toInteger(fce_GetSysdate("YYYY")));
        cboSStndYear.Value = Arg_sStndYear;
        cboSMbr.Value      = Arg_sMbrCd;

        fce_GetRaceTms(Arg_sStndYear, Arg_sMbrCd, "", "dsOutSRaceTms");
        return;
	}

	// È¸Â÷ ¸®½ºÆ® Á¶È¸½Ã
	if ( sServiceName == "common_race-service:searchRaceTms" ) {
        cboSRaceTms.Value = Arg_sTms;

        fce_GetRaceDayOrd(Arg_sStndYear, Arg_sMbrCd, Arg_sTms, "dsOutSDayOrd");
		return;
	}

	// ÀÏÂ÷ ¸®½ºÆ® Á¶È¸½Ã
	if ( sServiceName == "common_race-service:searchRaceDayOrd" ) {
        cboSDayOrd.Value = Arg_sDayOrd;

        fce_GetRace(Arg_sStndYear, Arg_sMbrCd, Arg_sTms, Arg_sDayOrd, "dsOutSRace");

        // 3ÀÏ ½ÃÇà½Ã
        if(dsOutSDayOrd.GetRowCount()==3){
			grdRacerList.SetColProp(12, "Width", 90);
			grdRaceRacer.SetColProp(6, "Width", 105);
        }

		return;
	}

	// °æÁÖ ¸®½ºÆ® Á¶È¸½Ã
	if ( sServiceName == "common_race-service:searchRace" ) {
        cboSRace.Value     = Arg_sRaceNo;

        dsOutRaceGap.AddColumn("RACE_GAP");
        for ( var i = 0; i < dsOutSRace.GetRowCount(); i++ ) {
            dsOutRaceGap.AddRow();
            dsOutRaceGap.SetColumn(i, "RACE_GAP", i);
        }
        cboRaceGap.Value = 0;

        fcSearchRacer();
		return;
	}

	// Á¶È¸ ÈÄ
	if ( sServiceName == "e02040011-service:searchRacer" ) {
        fnc_SetStatus(SNIS_BOA_0003, dsOutRacer.GetRowCount());
        taRsn.Text         = Arg_sRacerRsn;
        //mskAvgScr.Text     = truncate(dsOutRacer.GetColumn(0, "TMS_6_AVG_SCR"));
        //cboSRacerGrd.Value = dsOutRacer.GetColumn(0, "RACER_GRD_CD");

		if(dsOutRaceRacer.GetRowCount()== 7){

			grdRacerList.SetColProp(19, "Width", 20);

        }else if (dsOutRaceRacer.GetRowCount()== 8){ 

			grdRacerList.SetColProp(19, "Width", 20);
			grdRacerList.SetColProp(20, "Width", 20);

		}       

        return;
	}

	// Á¶È¸ ÈÄ
	if ( sServiceName == "e02040011-service:searchRacerList" ) {
        fnc_SetStatus(SNIS_BOA_0003, dsOutRacerList.GetRowCount());
        return;
	}

	//¿À·ù°¡ ¾øÀÌ Á¤»ó Ã³¸®µÇ¾ú´Ù¸é...
	fcPostProcess(sServiceName);
}

