/*-------------------------------------------------------------------------------------------------+
>>  Grid Double Click½Ã
+-------------------------------------------------------------------------------------------------*/
function grdRacerList_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var sRacerNo = dsOutRacerList.GetColumn(dsOutRacerList.currow, "RACER_NO");
	if ( fnc_IsNull(sRacerNo) ) {
        return;
	}

	var asReturn = Array(4);
	asReturn[0] = sRacerNo;
	asReturn[1] = taRsn.Text;
	asReturn[2] = dsOutRacerList.GetColumn(dsOutRacerList.currow, "MOT_NO");
	asReturn[3] = dsOutRacerList.GetColumn(dsOutRacerList.currow, "BOAT_NO");

	close(asReturn);
}

