/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
    dsOutRacerList.ClearData();

    if ( fnc_IsNull(Arg_sDayOrd) ) {
        chkEnterRacer.Enable = false;
    } else {
        chkEnterRacer.Enable = true;
    }
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchRacerCheck(obj) {
    if ( fnc_IsNull(Arg_sRacerNo) ) return false;

	return true;
}

function fcSearchRacer(obj) {
    if ( !fcSearchRacerCheck(obj) ) return;

	var sServiceName = "e02040011-service:searchRacer";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutRacer=dsOutRacer "
                     + "dsOutRaceRacer=dsOutRaceRacer ";

	fce_AddParam(Trim("STND_YEAR "), Arg_sStndYear);
	fce_AddParam(Trim("MBR_CD    "), Arg_sMbrCd   );
	fce_AddParam(Trim("TMS       "), Arg_sTms     );
	fce_AddParam(Trim("DAY_ORD   "), Arg_sDayOrd  );
	fce_AddParam(Trim("RACE_NO   "), Arg_sRaceNo  );
	fce_AddParam(Trim("RACER_NO  "), Arg_sRacerNo );

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	return true;
}

function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "e02040011-service:searchRacerList";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutRacerList=dsOutRacerList ";              

	fce_AddParam(Trim("STND_YEAR   "), cboSStndYear.Value );
	fce_AddParam(Trim("MBR_CD      "), cboSMbr.Value      );
	fce_AddParam(Trim("TMS         "), cboSRaceTms.Value  );
	fce_AddParam(Trim("DAY_ORD     "), cboSDayOrd.Value   );
	fce_AddParam(Trim("RACE_NO     "), cboSRace.Value     );
	fce_AddParam(Trim("NM_KOR      "), edRacerNm.Text     );
	fce_AddParam(Trim("RACER_GRD_CD"), cboSRacerGrd.Value );
	fce_AddParam(Trim("AVG_SCR     "), mskAvgScr.Text     );
	fce_AddParam(Trim("ENTER_RACER "), chkEnterRacer.Value);
	fce_AddParam(Trim("RACE_GAP    "), cboRaceGap.Value   );
	fce_AddParam(Trim("RACE_REG_NO "), cboRaceRegNo.Value );

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

